        "Search": function() {
            // FOR SCOPE
            var _this = this;

            //OPENING SEARCH
            $("#gb-search-control").on('click keypress', function(event){
                var searchControl = this;

                if(_this.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    if(!$(this).hasClass("open")){
                       openSearch(searchControl)
                    }
                }
            })

            //CLOSING SEARCH
            $("#gb-search").hover(function(){

            }, function(){
                var tab = false;
                closeSearch(tab);
            })

            $("#close-search").on('click keypress', function(event){
                //var searchControl = this;

                if(_this.AllyClick(event) === true){
                    closeSearch();
                }
            })

            function closeSearch(tab){
                var tab = tab;
                $("#gb-search-control").removeClass("open").attr("aria-expanded","false");
                $("#gb-search").removeClass("open").attr("aria-hidden","true");
                $("#gb-search-input, #gb-search-button").attr("tabindex","-1");
                if(tab != true){
                    $("#gb-search-control").focus();
                } else {
                    $(".cs-mystart-dropdown.would-like-to .cs-selector").focus();
                }

            }

            function openSearch(searchControl){
                var searchControl = searchControl;
                $(searchControl).addClass("open").attr("aria-expanded","true");
                $("#gb-search").addClass("open").attr("aria-hidden","false");
                $("#gb-search-input").attr("tabindex","0");
                $("#gb-search-button").attr("tabindex","0");
                setTimeout(function(){
                	$("#gb-search-input").focus();
                }, 500)
            }

            //SEARCH FORM SUBMISSION
            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "What can we help you find?" && $.trim($("#gb-search-input").val()) != "") {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
            });

            //SEARCH INPUT TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "What can we help you find?") {
                    $(this).val("");
                }
            });

            $("#gb-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("What can we help you find?");
                }
            });

            //SEARCH KEYBOARD NAV
            $("#gb-search-input").on("keydown", function(e){
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    case _this.KeyCodes.tab:
                        if(!e.shiftKey){
                            e.preventDefault(); 
                            $("#gb-search-button").focus();
                        } else {
                            e.preventDefault(); 
                            closeSearch();
                            $("#gb-search-control").focus();
                        }
                    break;
                }
            });
            $("#gb-search-button").on("keydown", function(e){
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    case _this.KeyCodes.tab:
                        if(!e.shiftKey){
                            e.preventDefault(); 
                            var tab = true;
                            closeSearch(tab);
                        }
                    break;
                }
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

