	function pgScreenSize(){
		var getWidth = $(window).width();
		var hex =  '#710F12';
		if (getWidth >= 1024) { // 960+
			multimediaRotator();
			if(!$('#hp-middle #hp-video-display').length){
				$('#hp-middle').append($('#hp-video-display'));
			}
			$("#hp-content .ui-widget-header, #hp-tab-view .ui-widget-header, #hp-video-display .ui-widget-header").css("box-shadow", "none");
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			multimediaRotator();
			if(!$('#hp-content #hp-video-display').length){
				$('#hp-content').prepend($('#hp-video-display'));
			}
			appView(["#hp-content"]);
			$("#hp-content .ui-widget-header").css({
				"border-color": "rgba(" + hexToRgb( hex ) +", 0.3)",
				"box-shadow": "0px 0px 5px rgba(" + hexToRgb( hex ) +", 0.3) inset"
			});
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			multimediaRotator();
			if(!$('#hp-content #hp-video-display').length){
				$('#hp-content').prepend($('#hp-video-display'));
			}
			appView(["#hp-content", "#hp-tab-view"]);
			$("#hp-content .ui-widget-header, #hp-tab-view .ui-widget-header").css({
				"border-color": "rgba(" + hexToRgb( hex ) +", 0.3)",
				"box-shadow": "0px 0px 5px rgba(" + hexToRgb( hex ) +", 0.3) inset"
			});

			$('#gb-channel-list-announcement').appendTo('#gb-channel-list');

			addToMobileMenu();

			if($('#rs-menu').length > 1){
				$('.rs-menu-option.channels .rs-option-header h3').text('Website Menus');
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			multimediaRotator();
			if(!$('#hp-content #hp-video-display').length){
				$('#hp-content').prepend($('#hp-video-display'));
			}
			appView(["#hp-content", "#hp-tab-view"]);
			$("#hp-content .ui-widget-header, #hp-tab-view .ui-widget-header").css({
				"border-color": "rgba(" + hexToRgb( hex ) +", 0.3)",
				"box-shadow": "0px 0px 5px rgba(" + hexToRgb( hex ) +", 0.3) inset"
			});

			$('#gb-channel-list-announcement').appendTo('#gb-channel-list-outer');

			addToMobileMenu();

			if($('#rs-menu').length > 1){
				$('.rs-menu-option.channels .rs-option-header h3').text('Website Menus');
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			if(!$('#hp-content #hp-video-display').length){
				$('#hp-content').prepend($('#hp-video-display'));
			}
			appView(["#hp-content", "#hp-tab-view"]);
			$("#hp-content .ui-widget-header, #hp-tab-view .ui-widget-header").css({
				"border-color": "rgba(" + hexToRgb( hex ) +", 0.3)",
				"box-shadow": "0px 0px 5px rgba(" + hexToRgb( hex ) +", 0.3) inset"
			});

			$('#gb-channel-list-announcement').appendTo('#gb-channel-list-outer');

			addToMobileMenu();
			if($('#rs-menu').length > 0){
				$('.rs-menu-option.channels .rs-option-header h3').text('Website Menus');
			}
		}

		//Header text HOURLY CHANGE
		var halfHeight = $('#gb-logo').outerHeight() / 2;
		var halfTextHeight = $('#gb-header .gb-sitename').outerHeight() / 2;
		$('#gb-header .gb-sitename').css('top', halfHeight - halfTextHeight);
		stickyHeader(1024); //continues to check if we need to hide the sticky menu
	}

