			}
		}
		if($('.sw-mystart-button.manage').length > 0){
			$('#gb-user-options ul').append("
" + $('.sw-mystart-button.manage').html() + "");
		}
        $("#sw-mystart-outer .sw-mystart-button.manage").remove();

		if($('#sw-mystart-mypasskey').length > 0){
			$('#gb-header .gb-global-links').append($('#sw-mystart-mypasskey > a'));
			$('#gb-header .gb-global-links > a').wrap('
');
		}

		$('#sw-myaccount-list li').appendTo('#gb-user-options ul');

		$("#gb-user-options ul").prepend('
Email');

		$('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#gb-school-dropdown ul');

		$('#gb-search').append('');
		$('#gb-search input[type="text"]').attr('placeholder', '');

		$('#gb-search #gb-search-icon').click(function(e){
			$('#gb-search input[type="text"]').animate({'width': 'toggle'});
		});

		if( $(window).width > 1023 ){
			//Header text 
			var halfHeight = $('#gb-header').outerHeight() / 2;
			var halfTextHeight = $('#gb-header .gb-sitename').outerHeight() / 2;
			$('#gb-header .gb-sitename').css('top', halfHeight - halfTextHeight);
			/* END HOURLY CHANGE */
		}

		if(typeof String.prototype.trim !== 'function') { //polyfill for IE8 and trim
		  String.prototype.trim = function() {
		    return this.replace(/^\s+|\s+$/g, ''); 
		  };
		}

		/* EDIT SITENAME */
		var siteName = $("#gb-logo .gb-sitename h1").text();
			//common school name endings to break on
		var possEnds = ["elementary", "junior", "jr.", "high", "jr./sr.", "school", "academy", "academies", "district"]
		var checkName = siteName.trim().split(" ");

		var siteNameBegin = "";
		var siteNameEnd = "";

			//go through each word until we find a common phrase to break on
		$.each(checkName, function(k, v){	
			if( $.inArray(v.toLowerCase().trim(), possEnds) >= 0 ){
				var arrPos = $.inArray(v.toLowerCase(), possEnds);
				siteNameEnd = siteName.substring(siteName.toLowerCase().indexOf(possEnds[arrPos]), siteName.length); //strip out the common part for the end
				siteNameBegin = siteName.substring(0, siteName.toLowerCase().indexOf(possEnds[arrPos])); //add the dynamic part to the beginning
				return false; //breaks out
			}
		});

		if(siteNameEnd === ""){
			siteNameBegin = siteName;
		}

		$(".gb-sitename h1").html(siteNameBegin + "" + siteNameEnd); //put the modified name back
        $("#gb-footer-logo .gb-sitename h2").html(siteNameBegin + "" + siteNameEnd); //put the modified name back

        $(".gb-sitename h2:empty").remove();  //added by Lyndsay - Case 00523067 - 4.20.17

		// ********** HEADER ********** //

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		$('li.sw-channel-item.first a').html('radnortsd');

		var announcementLeft = '!Announcement: ';
		$('#gb-channel-list-announcement div.announcements div.ui-article-description').prepend(announcementLeft);

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//DOC add read full story button to each headline
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(".ui-article-description", this).append('Read Full Story »');
        		});
        	});

        	modEvents();

        		//Checks if their is an announcement if not it will hide the box
        	if(!$('#gb-channel-list-announcement ul.ui-articles li').length){
        		$('#gb-channel-list-announcement').css('display', 'none');
        	}
		if($('.upcomingevents').length){ //kluge to get the left side of the cal events to look pretty.. could not use pseudo because of need to use hexToRgb for element set color and transparency
			$('.upcomingevents .ui-widget-detail ul.ui-articles').append($('#gb-events-left-bg').removeClass("hidden"));	
		}        	
        	$('#hp-content .ui-widget-header h1, #hp-video-display .ui-widget-header h1').append('&#xe609');
		$('#hp-tab-view .ui-widget-header h1').append('&#xe609');

			//Find all app headers. 
			//If their label doesnt contain anything then remove its bottom border.
			//Or if the header has no children (i.e. no h1) (it is slightly redudant to check this, but assumptions can be dangerous and cause problems)
		$('.ui-widget-header').each(function(e){
			if($(this).children().length <= 0 || $(this).text() == ""){
				$(this).css("border-bottom", "none");
			}
		});

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		$("a#feedback").text($("div#sw-footer-links li:eq(0) a").text()).attr("href", $("div#sw-footer-links li:eq(0) a").attr("href"));
		$("a#terms").text($("div#sw-footer-links li:eq(2) a").text()).attr("href", $("div#sw-footer-links li:eq(2) a").attr("href"));
		$("a#policy").text("Privacy Policy").attr("href", $("div#sw-footer-links li:eq(1) a").attr("href"));		
		$("p#copyright").text($("div#sw-footer-copyright").text());

		addFramesetTranslate();

		$('#gb-footer-translate').hover(function(e){ //in
			$('#gb-footer-translate p span.icon-font').html("");
			$("#gb-footer-translate .languages").removeClass("hidden");
		}, function(e){ //out
			$('#gb-footer-translate p span.icon-font').html("");
			$("#gb-footer-translate .languages").addClass("hidden");
		})
		.click(function(e){
			if($("#gb-footer-translate .languages").hasClass("hidden")){
				$('#gb-footer-translate p span.icon-font').html("");
				$("#gb-footer-translate .languages").removeClass("hidden");
			}else{
				$('#gb-footer-translate p span.icon-font').html("");
				$("#gb-footer-translate .languages").addClass("hidden");
