	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        var searchText = "I Am Looking For";
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", searchText);
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == searchText) {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val(searchText);
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

	/*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/
	function stickyHeader(shutoff){
		var getWidth = $(window).width();

		if( getWidth >= shutoff ){
			var target = "gb-channel-list-outer";

			var rect = document.getElementById(target).getBoundingClientRect(); //gets the target

			//check where the target is
			/*if(rect.top <= 170){ //make it sticky
				if( !$('#sticky-header').hasClass('on') ){
					$('#sticky-header-ghost').addClass('on').css('height', $('#sticky-header').height());
					$('#sticky-header').addClass('on');
				}

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("#sticky-header").outerHeight(true) + 23);
                    }
                }
			}

			if($(window).scrollTop() <= 120){ //unstick it
				$('#sticky-header').removeClass('on');
				$('#sticky-header-ghost').removeClass('on');
			}*/

            /* changed from the above to the below by Jeremy K - Case #04030302 */
            if($(window).scrollTop() >= 120){ //make it sticky
				if( !$('#sticky-header').hasClass('on') ){
					$('#sticky-header-ghost').addClass('on').css('height', $('#sticky-header').height());
					$('#sticky-header').addClass('on');
				}

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("#sticky-header").outerHeight(true) + 23);
                    }
                }
			}else if($('#sticky-header').hasClass('on')){ //unstick it
				$('#sticky-header').removeClass('on');
				$('#sticky-header-ghost').removeClass('on');
			}
		}else if( $('#sticky-header').hasClass('on') ){
			$('#sticky-header').removeClass('on');
			$('#sticky-header-ghost').removeClass('on');
		}
	}

	/*setupTabbedApps
		Use: This applies right borders to the headlinse articles as designated by the design
	*/
	function setupTabbedApps(){
		$('#hp-tab-view #sw-content-container1 > div').each(function(k1){
			if($(this).attr('id') !== "undefined"){
				var loop = 0;
				var len = $('#' + $(this).attr('id') + ' .headlines .ui-article').length;
				$('#' + $(this).attr('id') + ' .headlines .ui-article, #' + $(this).attr('id') + ' .announcements .ui-article').each(function(k2){
					if(loop % 2 === 0 && loop !== 0){
						$(this).css("border-right", "none");
					}else if( loop === len - 1){
						$(this).css("border-right", "none");
					}
					loop++;
				});
			}
		});
	} 

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	};
	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	};


	/*setupCustomOpacity
		Use: Will handle setting up any opacities that require the use of a template element set.
	*/
	function setupCustomOpacity(){
		var hex =  '#710F12';
		$('#gb-header-outer, #sw-mystart-outer').css("background-color", "rgba(" + hexToRgb( hex ) +", " + 0.77 + ")");
		$('#gb-footer-outer, #gb-legal-outer').css("background-color", "rgba(" + hexToRgb( hex ) +", " + 0.83 + ")");
		$('.gb-global-links-container ul.gb-global-links > li:not(#gb-footer-translate)').hover(function(e){ //in
			$(this).css('background-color', "rgba(" + hexToRgb( hex ) +", 0.8)");
		}, function(e){ //out
			$(this).css('background-color', hex);
		});

		$('.sp.column.one div.ui-widget.app.navigation .ui-widget-detail > ul').css("background-color", "rgba(" + hexToRgb( hex ) +", 0.1)");

		$("#hp-content .ui-widget-header h1,\
		#hp-middle #hp-video-display .ui-widget-header h1,\
		#sp-content div.ui-widget.app .ui-widget-header,\
		#spn-content div.ui-widget.app .ui-widget-header").css("border-color", "rgba(" + hexToRgb( hex ) +", 0.3)");


		setTimeout(function(e){
			$("div.tabs-container").css("border-color", "rgba(" + hexToRgb( hex ) +", 0.3)");
			customStyleSheet.insertRule("div.tabs-container .tab-button.active .tab-right { background-color: rgba(" + hexToRgb( hex ) +", 0.1)}", 0);
		}, 400);
	}

