function popUp(URL) {
        day = new Date();
        id = day.getTime();
        eval("page" + id + " = window.open(URL, '" + id + "', 'toolbar=0,scrollbars=1,location=0,statusbar=1,menubar=0,resizable=1,width=700,height=600,left = 440,top = 212');");
    }

$(document).ready(function () {
    $('.captionBG').hide();
    $('.captionBG:first').show();
    $('.carousel').waterwheelCarousel({
        autoPlay: 5000,
        speed: 500,
        flankingItems: 1,
        separation: 230,
        animationEasing: 'swing',
        movedToCenter: function ($newCenterItem) { $newCenterItem.next('.captionBG').show(); },
        movingFromCenter: function ($oldCenterItem) { $oldCenterItem.next('.captionBG').hide(); }
    });
});

