// Expand Type

$('#welcome-message-89').each(function(){

	var WMexpandTrigger = $(this).find('.welcome-message-expand-trigger');
	
	$(WMexpandTrigger).click(function(evt){
		evt.preventDefault();
		
		var WMactvExpandTrigger = $(this);
		var WMshortContent = $(this).parent().parent().find('.welcome-message-content-inner');
		var WMshortContentHTML = $(WMshortContent).html();
		var WMfullContent =  $(this).parent().parent().find('.welcome-message-content-full');
		var WMfullContentHTML =  $(WMfullContent).html();
		
		$(WMactvExpandTrigger).fadeOut();
				
		$(WMshortContent).html(WMfullContentHTML);
				
		var WMcloseTrigger = $(this).parent().parent().find('.welcome-message-collapse-trigger');
		
		$(WMcloseTrigger).click(function(evt){
			evt.preventDefault();
				$(WMshortContent).html(WMshortContentHTML);
				$(WMactvExpandTrigger).fadeIn();
		});
		
		
	});
});

