    mc.ready(function(){
        var sID = '1581';
        var sTitle = 'Homepage Graduates 1024x1024 [6]';
        randomCarousel(sID, sTitle)

        function randomCarousel(sID, sTitle) {
            var sNumber = 6;
            var sSelector = '#carousel' + sID
            var sClasses = 'col-lg-2 col-6';
            if (sTitle.indexOf('[') > 0) {
                sNumber = sTitle.split('[')[1].replace(']', '');
                sClasses = 'col-sm-4 col-6';
                switch (sNumber) {
                    case '1':
                        sClasses = 'col-12';
                        break;
                    case '2':
                        sClasses = 'col-lg-6 col-6';
                        break;
                    case '3':
                        sClasses = 'col-lg-4 col-12';
                        break;
                    case '4':
                        sClasses = 'col-lg-3 col-6';
                        break;
                    case '5':
                        sClasses = 'col-lg-2 col-12';
                        break;
                    case '6':
                        sClasses = 'col-lg-2 col-6';
                        break;
                    default:
                        sClasses = 'col-lg-2 col-6';
                }
            }
            $(sSelector + ' .random').addClass(sClasses);
            randomElements = $(sSelector + ' .random').get().sort(function () {
                return Math.round(Math.random()) - (sNumber / 10);
            }).slice(0, sNumber);
            $(randomElements).removeClass('d-none');
            $('img', randomElements).addClass('lazy').lazy();
            $(sSelector).addClass('show');
        };
    });

