function stickyNavMover() {
    if (window.innerWidth < 991)
        $(".SNLink").appendTo(".slicknav_nav");
    else
        $(".SNLink").appendTo(".StickyNavContainer");
}
$(document).ready(function () {
    stickyNavMover();
});
$(window).resize(function () {
    stickyNavMover();
});
