	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop":
				footerAccordionReset();
			break;
			case "768":
				footerAccordion();
			break;
			case "640":
				footerAccordion();
			break;
			case "480":
				footerAccordion();
			break;
			case "320":
            	footerAccordion();
			break;
		}
	}

    function updateLinkTargets() {
    	$("a[href][target='_blank']").each(function() {
        	$(this).attr("target", "_self");
        });
    }

    function customMinibase() {
    	var minibaseFilters = "";
        $(".ui-widget.app.minibase input[id*='sw-content-minibaselist'][value='C'] + .ui-widget-detail div[id*='sw-content-minibasefilter']").prepend('
');
    	$(".ui-widget.app.minibase input[id*='sw-content-minibaselist'][value='C'] + .ui-widget-detail div[id*='sw-content-minibasefilter'] .sw-flex-item").each(function(i) {
        	$(this).closest("div[id*='sw-content-minibasefilter']").find(".cs-custom-minibase-filters").append('
');
            $("> td", this).each(function() {
            	$(this).closest("div[id*='sw-content-minibasefilter']").find(".cs-custom-minibase-filter:last").append($(this).children());
            });
        });
    }

	function mediaRotator() {
		head.ready("mediaRotator", function(){
			if(!$("#sw-content-container13.region.ui-hp .rs-gallery-container").length){
				// MULTIMEDIA ROTATOR
				$("#sw-content-container13.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "imageWidth"              : 900,
                    "imageHeight"             : 410,
                    "efficientLoad"           : "yes",
                    "appHeader"               : "no",
                    "titles"                  : "yes",
                    "captions"                : "yes",
                    "galleryOverlay"          : "no",
                    "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                    "playPause"               : "yes",
                    "mainControls"            : "yes",
                    "contMoreLink"            : "yes",
                    "contMoreLinkText"        : "Read Moreabout this slide",
                    "descMoreLink"            : "no",
                    "descMoreLinkText"        : "",
                    "bullets"                 : "no",
                    "bulletControls"          : "no",
                    "thumbnails"              : "no",
                    "thumbnailWidth"          : 122,
                    "thumbnailHeight"         : 53,
                    "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                    "hoverPause"              : "yes",
                    "autoRotate"              : "yes",
                    "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
                    "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                    "fullScreenVideo"         : "yes",
                    "transitionType"          : "fade", // fade or slide
                    "transitionSpeed"         : 2,
                    "transitionDelay"         : 8,
                    "onImageLoad"             : function() { },
                    "allImagesLoaded"         : function() { },
                    "onThumbnailLoad"         : function() { },
                    "allThumbnailsLoaded"     : function() { },
                    "onTransitionStart"       : function() { },
                    "onTransitionEnd"         : function() { },
                    "watchVidClick"           : function() { },
                    "closeVidClick"           : function() { },
                    "allLoaded"               : function() {
                    	$("#hp-slideshow .rs-gallery-control.play-pause").insertBefore($("#hp-slideshow .rs-gallery-control.next"));
                    	checkGalleryControls();
                    }
				});
			}
		});
	}

    function checkGalleryControls() {
    	switch(csGetBreakPoint()) {
			case "desktop":
            	if(!csDesktopGalleryArrowsSet) {
                    if(!$("#hp-slideshow .rs-gallery-container > .rs-gallery-control.back").length) {
                        $("#hp-slideshow .rs-gallery-container").append($("#hp-slideshow .rs-gallery-control.back")).append($("#hp-slideshow .rs-gallery-control.play-pause")).append($("#hp-slideshow .rs-gallery-control.next"))

                        csDesktopGalleryArrowsSet = true;
                        csMobileGalleryArrowsSet = false;
                    }
                }
			break;
			case "768":
			case "640":
			case "480":
			case "320":
            	if(!csMobileGalleryArrowsSet) {
                    if(!$("#hp-slideshow .rs-gallery-images > .rs-gallery-control.back").length) {
                        $("#hp-slideshow .rs-gallery-images").append($("#hp-slideshow .rs-gallery-control.back")).append($("#hp-slideshow .rs-gallery-control.play-pause")).append($("#hp-slideshow .rs-gallery-control.next"))

                        csDesktopGalleryArrowsSet = false;
                        csMobileGalleryArrowsSet = true;
                    }
                }
			break;
		}
    }

    function checkPassKeyBtn() {
        switch(csGetBreakPoint()) {
			case "desktop":
            	if(!csDesktopPasskeyBtnIsSet) {
                	if(!$("#gb-mystart-account-links #sw-mystart-mypasskey").length) $("#gb-mystart-account-links").append($("#sw-mystart-mypasskey"));

                	csDesktopPasskeyBtnIsSet = true;
                    csMobilePasskeyBtnIsSet = false;
                }
            break;
			case "768":
			case "640":
			case "480":
			case "320":
            	if(!csMobilePasskeyBtnIsSet) {
					if(!$("#gb-channel-list #sw-mystart-mypasskey").length) $("#gb-channel-list").append($("#sw-mystart-mypasskey"));

                    csDesktopPasskeyBtnIsSet = false;
                    csMobilePasskeyBtnIsSet = true;
            	}
            break;
		}
    }

    function buildSchoolMenu(headerSocialMediaIcons) {
        var schools = '', sortedSchools = {};

        // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED TYPES IN THE ORDER WE WANT
        var expectedNameOrder = ["elementary schools", "intermediate schools", "middle schools", "high schools & special programs", "schools & programs of choice"];
        schoolList.sort(function(a, b) {
            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                return -1;
            }
            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                return 1;
            }
            return 0;
        });

        // SORT THE SCHOOLS INTO TYPE OBJECTS
        for(var type in schoolList) {
            if(schoolList[type].schoolVisible.toLowerCase() == 'true') {
                var thisType = schoolList[type].schoolType.toLowerCase();

                // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                if(schoolList[type].schoolType.indexOf(",") > -1) {
                    var subTypes = schoolList[type].schoolType.split(",");

                    // LOOP THROUGH THE SUB TYPES
                    for(var subType in subTypes) {
                        var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                        // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                        if(!sortedSchools.hasOwnProperty(thisSubType)) {
                            // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                            sortedSchools[thisSubType] = [schoolList[type]];
                        } else {
                            // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                            sortedSchools[thisSubType].push(schoolList[type]);
                        }
                    }
                }

                // SINGLE TYPE VALUE
                else {
                    // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                    if(!sortedSchools.hasOwnProperty(thisType)) {
                        // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                        sortedSchools[thisType] = [schoolList[type]];
                    } else {
                        // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                        sortedSchools[thisType].push(schoolList[type]);
                    }
                }
            }
        }

        // BUILD THE SCHOOL MENU STRUCTURE
        var schoolMenuTabs = '';
        var schoolMenuPanels = '
';
        $.each(sortedSchools, function(type, schools) {
            var typeName = type.replace(/\s/g, "-").replace(/&/g, "and");

            // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES

            // EDIT - 9.12.2016 - BRENTON KELLY
            // NEED TO PUT SCHOOLS IN A SPECIFIC ORDER UNDER HIGH SCHOOLS & OTHER PROGRAMS 
            // USING THE FRIENDLY WEB ADDRESS FIELD AS THE ORDER INDICATOR SINCE IT'S NOT BEING USED

            // UPDATED ON 3.20.18 BY BRENTON KELLY VIA CASE: 03093199
            if(type == "high schools & special programs") {
                var expectedHSSPNameOrder = [];
                var expectedHSSPNameOrderRaw = [
                    "Elsik Ninth Grade Center",
                    "Elsik High School",
                    "Hastings Ninth Grade Center",
                    "Hastings High School",
                    "Alief Taylor High School",
                    "Alief Learning Center",
                    "Crossroads",
                    "SOAR/LINC/NHS"
                ];

                // LOWERCASE ALL NAMES
                $.each(expectedHSSPNameOrderRaw, function(index, name) {
                    expectedHSSPNameOrder.push(name.toLowerCase());
                });

                schools.sort(function(a, b) {
                    if(expectedHSSPNameOrder.indexOf(a.schoolName.toLowerCase()) < expectedHSSPNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return -1;
                    }
                    if(expectedHSSPNameOrder.indexOf(a.schoolName.toLowerCase()) > expectedHSSPNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return 1;
                    }
                    return 0;
                });
            } else if(type == "schools & programs of choice") {
                var expectedSPOCNameOrder = [];
                var expectedSPOCNameOrderRaw = [
                    "Alief Early College High School",
                    "Alief International Academy",
                    "Kerr High School",
                    "Life Sciences Innovative Academy",
                    "Dual Language Program",
                    "Mandarin Immersion Program",
                    "Center for Advanced Careers"
                ];

                // LOWERCASE ALL NAMES
                $.each(expectedSPOCNameOrderRaw, function(index, name) {
                    expectedSPOCNameOrder.push(name.toLowerCase());
                });

                schools.sort(function(a, b) {
                    if(expectedSPOCNameOrder.indexOf(a.schoolName.toLowerCase()) < expectedSPOCNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return -1;
                    }
                    if(expectedSPOCNameOrder.indexOf(a.schoolName.toLowerCase()) > expectedSPOCNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return 1;
                    }
                    return 0;
                });
            } else {
                schools.sort(function(a, b) {
                    if(a.schoolName > b.schoolName) {
                        return 1;
                    }
                    if(a.schoolName < b.schoolName) {
                        return -1;
                    }
                    return 0;
                });
            }

            // BUILD TAB
            schoolMenuTabs += '
' + type + '';

            // BUILD PANEL
            schoolMenuPanels += '
';
            for(var school in schools) {
                schoolMenuPanels += '
' + schools[school].schoolName + '';
            }
            schoolMenuPanels += '';
        });
        schoolMenuTabs += '';
        schoolMenuPanels += '';

        // ADD THE COMPLETED MENU TO THE DOM
        $("#gb-school-list").html(schoolMenuTabs + schoolMenuPanels);

        // BIND THE TAB PANEL EVENTS
        SchoolMenuControls();

        // BUILD THE RESPONSIVE MENU NOW THAT WE HAVE THE SCHOOLS
        rsMenu(sortedSchools, headerSocialMediaIcons);
    }

    function buildSchoolCategoryColumns(schoolArray) {
    	// REFERENCE - http://codepen.io/uxbox/pen/fpLJl

        // DEFINE ARRAY THAT WILL HOLD NUMBER OF LINKS FOR EACH COLUMN
        var linksPerCol = [];

        // GET MINIMUM NUMBER OF SCHOOLS PER COLUMN
        var minLinkNum = Math.floor(schoolArray.length / 4);

        // GET THE NUMBER OF UNEVEN REMAINDERS
        var remainders = schoolArray.length - (minLinkNum * 4);

        // LOOP FOUR TIMES BECAUSE THERE ARE FOUR COLUMNS
        for (var i = 0; i < 4; i++) {
            // IF LOOP INDEX IS LESS THAN THE REMAINDER NUMBER
            if (i < remainders) {
                // ADD ONE TO THIS COLUMN LINK NUMBER SO THEN REMAINDER LINKS WILL BE DISTRIBUTED EVENLY
                linksPerCol[i] = minLinkNum + 1;
            } else {
                // THIS WILL BE THE MINIMUM
                linksPerCol[i] = minLinkNum;
            }
        }

        // NOW LOOP AGAIN FOUR TIMES TO BUILD THE COLUMNS - FOUR TIMES BECAUSE THERE ARE FOUR COLUMNS
        var schoolColumns = "";
        for (var i = 0; i < 4; i++) {
            schoolColumns += '';

            // LOOP THROUGH THE LINKS PER COLUMN ARRAY
            for (var j = 0; j < linksPerCol[i]; j++) {
                // BUILD THE NEXT ITEM INDEX
                var recordIndex = 0;
                for (var k = 0; k < i; k++) {
                    recordIndex += linksPerCol[k];
                }

                // BUILD THE LINK ITEM
                schoolColumns += '
' + schoolArray[j + recordIndex].siteName + '';
            }
            schoolColumns += '';
        }

        // RETURN THE ENTIRE STRUCTURE
        return schoolColumns;
    }

    function SchoolMenuControls() {
        // SCHOOLS BUTTON CLICK
        $(".sw-mystart-button.schools a").click(function(e) {
        	e.preventDefault();
            handleSchoolMenuButtonAction();
        });

        // SCHOOLS KEYDOWN
        $(".sw-mystart-button.schools a").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                handleSchoolMenuButtonAction();
            }

            if(e.shiftKey && e.keyCode == csKeyCodes.tab && $(this).attr("aria-expanded") == "true") {
            	e.preventDefault();

                $(".gb-school-list-tab:first").focus();
            }
        });

        // SCHOOL TAB FOCUS
        $(".gb-school-list-tab").focus(function() {
        	// ADJUST TAB ATTRIBUTES
            $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(this).attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");

            // ADJUST PANEL ATTRIBUTES
            $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
            $("#" + $(this).attr("aria-controls")).attr({"aria-hidden": "false"}).removeClass("active");
        });

        // IE TAB CLICK
        $(".ie .gb-school-list-tab span").click(function() {
        	$(this).parent().focus();
        });	

        // SCHOOL TAB KEYDOWN
        $(".gb-school-list-tab").keydown(function(e) {
        	handleTabListKey($(this), ".gb-school-list-tab", e);
        });

        // CLOSE MENU WITH ESC
        $(".gb-school-list-tab, .gb-school-list-panel").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.esc) {
            	e.preventDefault();

                $("#gb-school-list-outer").slideUp(function() {
                    // RESET ATTRIBUTES
                    $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                    $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
                }).attr("aria-hidden", "true");

                $(".sw-mystart-button.schools a").attr("aria-expanded", "false").focus();
            }
        });

        $(".gb-school-list-panel > ul > li:last-child a").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.tab) {
            	e.preventDefault();

                $(".sw-mystart-button.schools a").focus();
            }
        });

        // FOCUS LEAVES SCHOOL MENU
        $(document).keydown(function() {
        	// GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
            setTimeout(function() {
            	// IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
				if(!$("#gb-school-list-outer").find(":focus").length && !$(".sw-mystart-button.schools a").is(":focus")) {
                	// CLOSE THE MENU
                    $("#gb-school-list-outer").slideUp(function() {
                        // RESET ATTRIBUTES
                        $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                        $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
                    }).attr("aria-hidden", "true");

                    $(".sw-mystart-button.schools a").attr("aria-expanded", "false");
                }
            }, 100);
        });
    }

    function handleSchoolMenuButtonAction() {
    	// MENU IS NOT OPEN
        if($(".sw-mystart-button.schools a").attr("aria-expanded") == "false") {
            // MAKE SURE ALL ATTRIBUTES ARE RESET
            $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");

            // SET ELEMENTARY TAB AND PANEL AS ACTIVE
            $(".gb-school-list-tab:first").attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");
            $(".gb-school-list-panel:first").attr({"aria-hidden": "false"}).addClass("active");

            // OPEN THE MENU
            $("#gb-school-list-outer").slideDown(function() {
                $(".gb-school-list-tab:first").focus();
            }).attr("aria-hidden", "false");

            $(".sw-mystart-button.schools a").attr("aria-expanded", "true");
        }

        // MENU IS OPEN
        else {
            // CLOSE THE MENU
            $("#gb-school-list-outer").slideUp(function() {
                // RESET ATTRIBUTES
                $(".gb-school-list-tab").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                $(".gb-school-list-panel").attr({"aria-hidden": "true"}).removeClass("active");
            }).attr("aria-hidden", "true");

            $(".sw-mystart-button.schools a").attr("aria-expanded", "false");
        }
    }

	function dynamicContent() {
    	$("#skipLink").attr("tabindex", "0");
    	$("#sw-maincontent").removeAttr("tabindex");

        // ********** MYSTART ********** //
        // MYSTART DROPDOWNS
        $(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing");
			$(this).addClass("open");
		});
		$(".sw-mystart-dropdown").hover(function(e) {
			e.preventDefault();
		}, function() {
			$(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
			$(this).removeClass("open");
		});
        $(document).off("focus", ".sw-mystart-dropdown");
		$(document).on("focus", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
			$(this).addClass("open");
		});

        // ADJUST PASSKEY OVERLAY
        $(document).ajaxComplete(function() {
            if($("#ui-mypasskey-overlay").length && !$("#sw-mystart-mypasskey #ui-mypasskey-overlay").length) {
                $("#sw-mystart-mypasskey").append($("#ui-mypasskey-overlay"));
            }
        });

		// ********** HEADER ********** //
		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/TX01917308/Centricity/Template/GlobalAssets/images///logos/2019AliefTopWorkplace.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append("").removeClass("hidden");
			} else {
				$("#gb-logo").append("").removeClass("hidden");
			}
		}

		// ADD SITENAME
        if("AliefISD" != csDistrict) {
            var siteNameOne = jQuery.trim('');
            var siteNameTwo = jQuery.trim('');
            var siteNameBegin = "";
            var siteNameEnd = "";

            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Alief ISD";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }
        }

		// ********** CHANNEL BAR ********** //
        //DOC set icon for home channel
		var firstChannelText = $("li.sw-channel-item:first-child > a span").text().toLowerCase();
		if(firstChannelText == "home") $("li.sw-channel-item").first().addClass("home");
		//DOC set icon for calendar channel
		var lastChannelText = $("li.sw-channel-item:last > a span").text().toLowerCase();
		if(lastChannelText == "calendar") $("li.sw-channel-item").last().addClass("calendar").insertAfter($("li.sw-channel-item").first());

		$(".sw-channel-item").unbind('hover');
