        // ADJUST MORE LINKS AND VIEW CALENDAR LINKS
        $("a.more-link span").html("View all articles");
        $("a.more-link").each(function() {
        	if($(this).parent().hasClass("ui-articles")) {
            	$("+ .more-link-under", this).remove();
            	$(this).insertAfter($(this).parent());
            }
        });
        $("a.view-calendar-link span").html("View all events");

        // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });

        // FLIP EVENT TIME AND TITLE AND ADJUST DATES
        $(".ui-widget.app.upcomingevents .ui-articles > li").each(function() {
        	$(".ui-article-description", this).each(function() {
        		$(this).prepend($(".sw-calendar-block-title", this));
             });
            if($(".sw-calendar-block-date", this).text().indexOf(",") > -1) {
            	var eventDate = $(".sw-calendar-block-date", this).text().split(",");
                $(".sw-calendar-block-date", this).text(eventDate[0]);
            }
        });

        // CHECK IF HOMEPAGE COLUMN THREE IS EMPTY
        if(!$(".hp.column.three .ui-widget.app").length) {
        	$("#hp-content").addClass("no-right-column");
        }

        // REMOVE EMPTY STATS ITEMS
        $(".hp-stats-item.false").remove();

		// ********** FOOTER ********** //
		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));

        // BUILD GOOGLE MAP URL
		var mapURL = encodeURIComponent("4250 Cook Road Houston TX 77072");
		$("#gb-footer-map-link").attr("href", "//www.google.com/maps/place/" + mapURL);

        // SCROLL TO TOP BUTTON
        $("#gb-back-to-top").click(function(e){
			e.preventDefault();
			$("html, body").animate({ scrollTop: 0 }, "slow");
            $("li.sw-channel-item:first-child a").focus();
		});
        $("#gb-back-to-top").keydown(function(e){
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
            	$("html, body").animate({ scrollTop: 0 }, "slow");
            	$("li.sw-channel-item:first-child a").focus();
            }
		});
	}

