    function socialMediaIcons(headerSocialMediaIcons) {
    	var socialIcons = '
';
        var iconNum = 0;
        $.each(headerSocialMediaIcons, function(index, icon) {
        	if(icon.show) {
            	socialIcons += '';

                iconNum++;
            }
        });
        socialIcons += '';

        if(iconNum > 0) {
        	$(".gb-header.right").prepend(socialIcons);
        }
    }

    function buildStatsIcons() {
    	if(true && $(".hp").length) {
        	var statIcons = [
            	"Classroom/15.png",
                "Education Portal/3.png",
                "Student Program/3.png",
                "Athletics/7.png"
            ];

            var statIcon = "";
            for(var i in statIcons) {
                if($.trim(statIcons[i]).indexOf("cms") > -1) {
                    if(statIcons[i].indexOf('default-man') < 0 ){
                        statIcon = '';
                    }
                } else {
                    var splitSelection = statIcons[i].split("/");
                    var iconCat = $.trim(splitSelection[0]);
                    iconCat = iconCat.toLowerCase().replace(/\s+/g, "-");
                    var iconChoice = splitSelection[1].split(".");
                    iconChoice = iconChoice[0];
                    var iconSelection = iconCat + "-" + iconChoice;
                    statIcon = '';
                }

                $("#hp-stats .hp-stats-item").eq(i).prepend(statIcon);
            }
        }
    }

    function buildAudienceChannels() {
    	if(!isMyViewPage) {
            var audienceChannelTabs = "", audienceChannelPanels = "", i = 0;
            var audienceMegaMenuData = [
                {
                    "showTabImage": true,
                    "tabImageSrc": "/cms/lib/TX01917308/Centricity/Template/GlobalAssets/images///audience_channel_images/Schoology.jpg",
                    "tabTitle": "Schoology",
                    "tabTeaser": "Learning Management System for Alief ISD Students",
                    "tabLinkText": "SIGN IN",
                    "tabLinkUrl": "https://alief.schoology.com",
                    "tabLinkTarget": "_self"
                }, {
                    "showTabImage": true,
                    "tabImageSrc": "/cms/lib/TX01917308/Centricity/Template/GlobalAssets/images///audience_channel_images/Scorecard.jpg",
                    "tabTitle": "Local Accountability System",
                    "tabTeaser": "Learn how Alief ISD earned an A rating.",
                    "tabLinkText": "READ MORE",
                    "tabLinkUrl": "/las",
                    "tabLinkTarget": "_self"
                }, {
                    "showTabImage": true,
                    "tabImageSrc": "/cms/lib/TX01917308/Centricity/Template/GlobalAssets/images///audience_channel_images/Teacher.jpg",
                    "tabTitle": "Top Workplace",
                    "tabTeaser": "Alief ISD was named a Top Workplace by the Houston Chronicle.",
                    "tabLinkText": "CHOOSE ALIEF ISD",
                    "tabLinkUrl": "/career",
                    "tabLinkTarget": "_self"
                }, {
                    "showTabImage": true,
                    "tabImageSrc": "/cms/lib/TX01917308/Centricity/Template/GlobalAssets/images///audience_channel_images/Scorecard.jpg",
                    "tabTitle": "Local Accountability System",
                    "tabTeaser": "Learn how Alief ISD earned an A rating.",
                    "tabLinkText": "READ MORE",
                    "tabLinkUrl": "/las",
                    "tabLinkTarget": "_self"
                }
            ];

            // LOOP THROUGH THE CHANNELS AND FIND ONES THAT HAVE THE RESOURCES KEYWORD
            $("#gb-channel-list li.sw-channel-item").each(function() {
                if($("> a span", this).text().toLowerCase().indexOf("resources") > -1 && audienceChannels.length < 4) {
                	// ADDED THIS IF CHECK LATER TO HELP WITH CLIENT WITH ORGANIZING THESE CHANNELS
                    if($("> a span", this).text().toLowerCase().indexOf("student") > -1 || $("> a span", this).text().toLowerCase().indexOf("family") > -1 || $("> a span", this).text().toLowerCase().indexOf("community") > -1 || $("> a span", this).text().toLowerCase().indexOf("staff") > -1) {
                        // HIDE THIS CHANNEL IN THE CHANNEL BAR
                        $(this).hide().addClass("gb-audience");

                        var audienceSections = [], audienceSection = "";
                        if($("> .sw-channel-dropdown li", this).length) {
                            $("> .sw-channel-dropdown li", this).each(function() {
                                audienceSection = {"sectionName": $("> a span", this).text(), "sectionUrl": $("> a", this).attr("href")};
                                audienceSections.push(audienceSection);
                            });
                        }
                        var audienceChannel = {"channelName": $("> a span", this).text(), "sections": audienceSections};

                        // BUILD THE TAB
                        audienceChannelTabs += '
' + $("> a span", this).text().toLowerCase().replace("resources", "resources") + '';

                        // BUILD THE PANEL
                        audienceChannelPanels += '
';
                        audienceChannelPanels += 	'
';

                        if(audienceMegaMenuData[i].showTabImage) {
                            audienceChannelPanels += 	'';
                        }
                        audienceChannelPanels += 		'
' + audienceMegaMenuData[i].tabTitle + '';
                        audienceChannelPanels += 		'
' + audienceMegaMenuData[i].tabTeaser + '' + audienceMegaMenuData[i].tabLinkText + '';
                        audienceChannelPanels += 	'';
                        audienceChannelPanels += 	'
' + (($("> .sw-channel-dropdown li", this).length) ? '' + $("> .sw-channel-dropdown", this).clone().html() + '' : '') + '';
                        audienceChannelPanels += '';

                        i++
                        csAudienceChannelsExist = true;
                        audienceChannels.push(audienceChannel);
                    }
                }
            });

            // IF ANY RESOURCES CHANNELS WERE FOUND
            if(audienceChannelTabs != "") {
                $("#gb-audience-channels-inner").html('' +
                    '' + audienceChannelTabs + '' +
                    '
' + audienceChannelPanels + '' +
                '');

                $(".gb-audience-panel-column.two").each(function() {
                	$("li[id*='navs']", this).removeAttr("id");
                });
            }

            audienceTabControls();
        }
    }

    function audienceTabControls() {
    	// TAB FOCUS
        $(".gb-audience-tab").focus(function() {
        	if(!$(this).hasClass("active")) {
                // ADJUST TAB ATTRIBUTES
                $(".gb-audience-tab").removeClass("active").attr({"aria-selected": "false", "tabindex": "-1"});
                $(this).addClass("active").attr({"aria-selected": "true", "tabindex": "0"});

                // ADJUST PANEL ATTRIBUTES
                $(".gb-audience-panel").stop(true, true);
                $(".gb-audience-panel").removeClass("active").attr("aria-hidden", "true").removeAttr("style");
                $("#" + $(this).attr("aria-controls")).slideDown(function() {
                    $(this).addClass("active").attr("aria-hidden", "false").removeAttr("style");
                });
            }
        });

        // TAB KEYDOWN
        $(".gb-audience-tab").keydown(function(e) {
        	handleTabListKey($(this), ".gb-audience-tab", e);
        });

        // TAB CLICK
        $(document).on("mousedown", ".gb-audience-tab[aria-selected='true']", function(e) {
            $(".gb-audience-panel").slideUp(function() {
            	$(".gb-audience-tab").removeClass("active").attr({"aria-selected": "false", "tabindex": "-1"});
            	$(".gb-audience-tab:first-child").attr("tabindex", "0");
            	$(this).removeClass("active").attr("aria-hidden", "true").removeAttr("style");
            });
        }).on("mousedown", ".gb-audience-tab[aria-selected='false']", function(e) {
        	$(this).focus();
        });

        // MOUSEOUT
        $("#gb-audience-channels").mouseleave(function() {
            $(".gb-audience-panel").slideUp(function() {
            	$(".gb-audience-tab").removeClass("active").attr({"aria-selected": "false", "tabindex": "-1"}).blur();
            	$(".gb-audience-tab:first-child").attr("tabindex", "0");
            	$(this).removeClass("active").attr("aria-hidden", "true").removeAttr("style");
            });
        });

        // FOCUS LEAVES AUDIENCE CONTAINER
        $(document).keyup(function() {
        	// GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
            setTimeout(function() {
            	// IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
				if(!$("#gb-audience-channels").find(":focus").length) {
                    $(".gb-audience-panel").slideUp(function() {
                    	$(".gb-audience-tab").removeClass("active").attr({"aria-selected": "false", "tabindex": "-1"});
                    	$(".gb-audience-tab:first-child").attr("tabindex", "0");
                    	$(this).removeClass("active").attr("aria-hidden", "true").removeAttr("style");
                    });
                }
            }, 100);
        });
    }

    function handleTabListKey(tab, tabClass, event) {
    	var lastIndex = $(tabClass).length - 1;

        switch(event.keyCode){
        	// CONSUME LEFT AND UP ARROWS
            case csKeyCodes.left:
            case csKeyCodes.up:
            	event.preventDefault();

                // FOCUS THE PREVIOUS TAB
                if (tab.index() == 0) { 
                    $(tabClass + ":last-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() - 1).focus();
                } 
            break;

            // CONSUME RIGHT AND DOWN ARROWS
            case csKeyCodes.right:
            case csKeyCodes.down:
            	event.preventDefault();

                // FOCUS THE NEXT TAB
                if (tab.index() == lastIndex) { 
                    $(tabClass + ":first-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() + 1).focus();
                }
            break;

            // CONSUME HOME KEY
            case csKeyCodes.home:
            	event.preventDefault();

                // FOCUS THE FIRST TAB
                $(tabClass + ":first-child").focus();
            break;

            // CONSUME END KEY
            case csKeyCodes.end:
            	event.preventDefault();

                // FOCUS THE LAST TAB
                $(tabClass + ":last-child").focus();
            break;
        }
    }

    function stickyChannelBar() {
		if(!isMyViewPage) {
			switch(csGetBreakPoint()) {
				case "desktop":
					var gbNavOffSet = $("nav").offset().top;
					if ($(window).scrollTop() <= gbNavOffSet) {
						if(csChannelBarIsSticky) {
							$("#gb-channel-list-outer").removeClass("sticky");
							$("nav").css("height", "auto");

							csChannelBarIsSticky = false;
						}
					} else {
						$("nav").css("height", $("#gb-channel-list-outer").height());

						if(!csChannelBarIsSticky) {
							$("#gb-channel-list-outer").addClass("sticky");

							csChannelBarIsSticky = true;
						}
					}
				break;
				case "768": case "640": case "480": case "320":
					if(csChannelBarIsSticky) {
						$("#gb-channel-list-outer").removeClass("sticky");
						$("nav").css("height", "auto");

						csChannelBarIsSticky = false;
					}
				break;
			}
		}
	}

    function stickyHomepageAudienceTabs() {
		if(!isMyViewPage && $(".hp").length && csAudienceChannelsExist) {
			switch(csGetBreakPoint()) {
				case "desktop":
					var gbAudienceTabOffSet = $("#gb-audience-channels-outer").offset().top - $("#gb-channel-list-outer").height() + ($("#gb-audience-tabs").height() / 2);
                    if ($(window).scrollTop() <= gbAudienceTabOffSet) {
						if(csHomepageAudienceTabsAreSticky) {
							$("#gb-audience-channels").removeClass("sticky").removeAttr("style");
							$("#gb-audience-channels-outer").css("height", "auto");

							csHomepageAudienceTabsAreSticky = false;
						}
					} else {
						$("#gb-audience-channels-outer").css("height", "99px");

						if(!csHomepageAudienceTabsAreSticky) {
							$("#gb-audience-channels").addClass("sticky").css("top", $("#gb-channel-list-outer").height());

							csHomepageAudienceTabsAreSticky = true;
						}
					}

                    $("#sw-maincontent").css({
                        "display": "block",
                        "position": "relative",
                        "top": "-" + ($("#gb-channel-list-outer").outerHeight(true) + $("#gb-audience-channels").outerHeight(true)) + "px"
                    });
				break;
				case "768": case "640": case "480": case "320":
					if(csHomepageAudienceTabsAreSticky) {
						$("#gb-audience-channels").removeClass("sticky").removeAttr("style");
						$("#gb-audience-channels-outer").css("height", "auto");

						csHomepageAudienceTabsAreSticky = false;
					}

                    $("#sw-maincontent").removeAttr("style");
				break;
			}
		}
	}

    function stickySubpageAudienceTabs() {
		if(!isMyViewPage && $(".sp").length && csAudienceChannelsExist) {
			switch(csGetBreakPoint()) {
				case "desktop":
					var gbAudienceTabOffSet = $("#gb-audience-channels-outer").offset().top - $("#gb-channel-list-outer").height();
                    if ($(window).scrollTop() <= gbAudienceTabOffSet) {
						if(csHomepageAudienceTabsAreSticky) {
							$("#gb-audience-channels").removeClass("sticky").removeAttr("style");
							$("#gb-audience-channels-outer").css("height", "auto");

							csHomepageAudienceTabsAreSticky = false;
						}

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
					} else {
						$("#gb-audience-channels-outer").css("height", $("#gb-audience-channels-inner").height());

						if(!csHomepageAudienceTabsAreSticky) {
							$("#gb-audience-channels").addClass("sticky").css("top", $("#gb-channel-list-outer").height());

							csHomepageAudienceTabsAreSticky = true;
						}

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            if($(".wcm-controls").hasClass("wcm-stuck")) {
                                $(".wcm-controls").css("margin-top", ($("#gb-channel-list-outer").outerHeight(true) + $("#gb-audience-channels").outerHeight(true)));
                            }
                        }
					}

                    $("#sw-maincontent").css({
                        "display": "block",
                        "position": "relative",
                        "top": "-" + ($("#gb-channel-list-outer").outerHeight(true) + $("#gb-audience-channels").outerHeight(true)) + "px"
                    });
				break;
				case "768": case "640": case "480": case "320":
					if(csHomepageAudienceTabsAreSticky) {
						$("#gb-audience-channels").removeClass("sticky").removeAttr("style");
						$("#gb-audience-channels-outer").css("height", "auto");

						csHomepageAudienceTabsAreSticky = false;
					}

                    $("#sw-maincontent").removeAttr("style");
                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        $(".wcm-controls").css("margin-top", "0px");
                    }
				break;
			}
		}
	}

