    function rsMenu(sortedSchools, headerSocialMediaIcons) {
    	// BUILD THE TRANSLATE ITEMS
        var pageURL = encodeURIComponent(window.location.href);
        var translateItems =	'1,Español (Spanish),//translate.google.com/translate?u=' + pageURL + '&langpair=en|es&hl=en&anno=0&ie=UTF8,_self;' +
								'1,(Vietnamese),//translate.google.com/translate?u=' + pageURL + '&langpair=en|vi&hl=en&anno=0&ie=UTF8,_self;' +
                        		'1,(Traditional Chinese),//translate.google.com/translate?u=' + pageURL + '&langpair=en|zh-TW&hl=en&anno=0&ie=UTF8,_self;' +
								'1,(Arabic),//translate.google.com/translate?u=' + pageURL + '&langpair=en|ar&hl=en&anno=0&ie=UTF8,_self;' +
                              	'1,(Burmese),//translate.google.com/translate?u=' + pageURL + '&langpair=en|my&hl=en&anno=0&ie=UTF8,_self;' +
                            	'1,(Urdo),//translate.google.com/translate?u=' + pageURL + '&langpair=en|ur&hl=en&anno=0&ie=UTF8,_self;' +
								'1,(Yoruba),//translate.google.com/translate?u=' + pageURL + '&langpair=en|yo&hl=en&anno=0&ie=UTF8,_self;' +
                              	'1,View Additional Languages,//translate.google.com/translate?u=' + pageURL + '&langpair=af|en&hl=en&anno=0&ie=UTF8,_self;';

        // DEFINE THE HEADER COUNTER
        // NEEDS TO BE DYNAMIC BECAUSE WE CAN'T BE SURE HOW MANY AUDIENCE CHANNELS WE'LL HAVE
        // START WITH 1 BECAUSE TRANSLATE IS A SURE THING
        var headerCounter = 1;

        // BUILD THE SCHOOL MENU STRUCTURE
        var schoolHeaders = "";
        var schoolItems = "";
        $.each(sortedSchools, function(type, schools) {
            var typeName = type.replace(/\s/g, "-").replace(/&/g, "and");

            // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES

            // EDIT - 9.12.2016 - BRENTON KELLY
            // NEED TO PUT SCHOOLS IN A SPECIFIC ORDER UNDER HIGH SCHOOLS & OTHER PROGRAMS 
            // USING THE FRIENDLY WEB ADDRESS FIELD AS THE ORDER INDICATOR SINCE IT'S NOT BEING USED

            // UPDATED ON 3.20.18 BY BRENTON KELLY VIA CASE: 03093199
            if(type == "high schools & special programs") {
                var expectedHSSPNameOrder = [];
                var expectedHSSPNameOrderRaw = [
                    "Alief Early College High School",
                    "Alief International Academy",
                    "Kerr High School",
                    "Life Science Innovative Academy",
                    "Elsik Ninth Grade Center",
                    "Elsik High School",
                    "Hastings Ninth Grade Center",
                    "Hastings High School",
                    "Alief Taylor High School",
                    "Alief Learning Center",
                    "Crossroads",
                    "SOAR/LINC/NHS"
                ];

                // LOWERCASE ALL NAMES
                $.each(expectedHSSPNameOrderRaw, function(index, name) {
                    expectedHSSPNameOrder.push(name.toLowerCase());
                });

                schools.sort(function(a, b) {
                    if(expectedHSSPNameOrder.indexOf(a.schoolName.toLowerCase()) < expectedHSSPNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return -1;
                    }
                    if(expectedHSSPNameOrder.indexOf(a.schoolName.toLowerCase()) > expectedHSSPNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return 1;
                    }
                    return 0;
                });
            } else if(type == "schools & programs of choice") {
                var expectedSPOCNameOrder = [];
                var expectedSPOCNameOrderRaw = [
                    "Alief Early College High School",
                    "Alief International Academy",
                    "Kerr High School",
                    "Life Science Innovative Academy",
                    "Dual Language Program",
                    "Mandarin Immersion Program",
                    "Center for Advanced Careers"
                ];

                // LOWERCASE ALL NAMES
                $.each(expectedSPOCNameOrderRaw, function(index, name) {
                    expectedSPOCNameOrder.push(name.toLowerCase());
                });

                schools.sort(function(a, b) {
                    if(expectedSPOCNameOrder.indexOf(a.schoolName.toLowerCase()) < expectedSPOCNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return -1;
                    }
                    if(expectedSPOCNameOrder.indexOf(a.schoolName.toLowerCase()) > expectedSPOCNameOrder.indexOf(b.schoolName.toLowerCase())) {
                        return 1;
                    }
                    return 0;
                });
            } else {
                schools.sort(function(a, b) {
                    if(a.schoolName > b.schoolName) {
                        return 1;
                    }
                    if(a.schoolName < b.schoolName) {
                        return -1;
                    }
                    return 0;
                });
            }
            // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
            /*schools.sort(function(a, b) {
                if(a.schoolName > b.schoolName) {
                    return 1;
                }
                if(a.schoolName < b.schoolName) {
                    return -1;
                }
                return 0;
            });*/

            headerCounter++; // WE HAVE A HEADER SO INCREMENT BY 1
            schoolHeaders += type + ";";

            for(var school in schools) {
            	schoolItems += (headerCounter) + ',' + schools[school].schoolName + ',' + schools[school].schoolUrl + ',_self;';
            }
        });

        // BUILD THE GLOBAL ICONS
        var globalIcons = [
			["ENROLL TODAY!", "https://www.aliefisd.net/enroll", "_self"],
			["ALIEF LEARNS", "https://www.aliefisd.net/learn", "_self"],
			["WHY ALIEF?", "https://www.aliefisd.net/why", "_self"],
			["CONTACT US", "https://www.aliefisd.net/contact", "_self"]
		];
		var globalIconNum = ("4" == "4;3;2;1;0") ? 0 : "4";
		var globalLinksHeader = "", globalLinks = "";

        if(globalIconNum > 0) {
			globalLinksHeader = "Global Links";
            headerCounter++; // WE HAVE A HEADER SO INCREMENT BY 1
		}

		for(var i = 0; i < globalIconNum; i++) {
			var linkCheck = (globalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : globalIcons[i][1];
			globalLinks += (headerCounter) + ',' + globalIcons[i][0] + ',' + linkCheck + ',' + globalIcons[i][2] + ';';
		}

        // BUILD THE SOCIAL MEDIA LINKS
        var socialLinkHeader = "";
        var socialLinkNum = 0;
        var socialLinks = ''
        $.each(headerSocialMediaIcons, function(index, icon) {
        	if(icon.show) {
            	socialLinks += (headerCounter + 1) + ',' + icon.label + ',' + icon.url + ',_blank;';

                socialLinkNum++;
            }
        });

        if(socialLinkNum > 0) {
        	socialLinkHeader = ((globalIconNum > 0) ? ";" : "") + "Social Media";
        	headerCounter++; // WE HAVE A HEADER SO INCREMENT BY 1
        }

    	head.ready("rsMainMenu", function() {
            // RESPONSIVE MAIN MENU
            $("body").csRsMenu({
                "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
                "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
                "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
                "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
                "accentColor"           : "#BABABA", // DEFAULT - #BABABA
                "menuButtonParent"      : "#gb-channel-list",
                "showMenuBtnText"       : "yes",
                "menuBtnText"           : "MENU",
                "showSchools"           : "no",
                "showTranslate"         : "no",
                "translateId"           : "none",
                "showAccount"           : "yes",
                "extraMenuOptions"      : "Translate;" + schoolHeaders + globalLinksHeader + socialLinkHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
                "extraMenuItems"        : translateItems + schoolItems + globalLinks + socialLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
                "siteID"                : "4",
                "allLoaded"             : function(){ }
            });
		});
    }

