    function footerAccordion() {
		if(!csFooterAccordionIsSet) {
			// SET ROLES AND ATTRIBUTES
            $("#gb-footer-bottom .gb-footer.true").each(function() {
            	$(".gb-footer-links", this).attr("aria-hidden", "true");
                $("> h2", this).attr("role", "button");
            });

			// ACCORDION HEADER CLICK
			$("#gb-footer-bottom .gb-footer.true > h2").click(function(){
                $(this).toggleClass("open");
                $(this).next(".gb-footer-links").slideToggle("300", "swing");
                $(this).next(".gb-footer-links").toggleClass("open");
                var ariaState = ($(this).hasClass("open")) ? "false" : "true";
                $(this).next(".gb-footer-links").attr("aria-hidden", ariaState);
			});

			csFooterAccordionIsSet = true;
		}
	}

	function footerAccordionReset() {
		if(csFooterAccordionIsSet) {
			// REMOVE ROLES AND ATTRIBUTES
            $("#gb-footer-bottom .gb-footer.true").each(function() {
            	$(".gb-footer-links", this).removeAttr("aria-hidden").removeClass("open").removeAttr("style");
                $("> h2", this).removeAttr("role").removeClass("open");
            });

            $("#gb-footer-bottom .gb-footer.true > h2").unbind("click");

			csFooterAccordionIsSet = false;
		}
	}

