	function searchControls() {
        $("#gb-search-button").click(function(e){
            e.preventDefault();
            animateSearch();
        });
        $("#gb-search-button").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
            	animateSearch();
            }
        });
        $("#gb-search-input").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter) {
            	e.preventDefault();
            	var searchForm = $(this).parent();
                if($.trim($("#gb-search-input", searchForm).val()) != "") {
            		window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input", searchForm).val();
                }
            }
        });
        var searchTimer = 0;
        $("#gb-search").mouseenter(function() {
        	clearTimeout(searchTimer);
        });
        $("#gb-search").mouseleave(function() {
        	searchTimer = setTimeout(function(){
            	$("#gb-search").animate({
                    width: "55px"
                }, 500, function() {
                    $("#gb-search-button").attr("aria-label", "Open search").focus();
                    $("#gb-search").removeClass("open");
                    $("#gb-search-form label, #gb-search-input").attr("aria-hidden", "true");
                });
            }, 1000);
        });
	}

    function animateSearch() {
        if(!$("#gb-search").hasClass("open")) {
            $("#gb-search-button").attr("aria-label", "Close search");
            $("#gb-search").addClass("open");
            $("#gb-search-form label, #gb-search-input").attr("aria-hidden", "false");

            var searchWidth = $("#gb-channel-list").width() - $("li.sw-channel-item.home").width() - $("li.sw-channel-item.calendar").width();
            $("#gb-search").animate({
                width: searchWidth
            }, 500, function() {
                $("#gb-search-input").focus();
            });
        } else {
            $("#gb-search").animate({
                width: "55px"
            }, 500, function() {
                $("#gb-search-button").attr("aria-label", "Open search");
                $("#gb-search").removeClass("open");
                $("#gb-search-form label, #gb-search-input").attr("aria-hidden", "true");
            });
        }
    }

    function checkSearch() {
    	if($("#gb-search").hasClass("open")) {
        	 $("#gb-search").css("width", ($("#gb-channel-list").width() - $("li.sw-channel-item.home").width() - $("li.sw-channel-item.calendar").width()));
        }
    }

	function calendarListView() {
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

