                                            && kc !== 8                     // bs
                                            && kc !== 9                     // tab
                                            && kc !== 189                   // -
                                            && (kc !== 190
                                                || s.$.val().match(/\./))   // . allowed once
                                            && e.preventDefault();

                                            // arrows
                                            if ($.inArray(kc,[37,38,39,40]) > -1) {
                                                e.preventDefault();

                                                var v = s.o.parse(s.$.val()) + kv[kc] * m;
                                                s.o.stopper && (v = max(min(v, s.o.max), s.o.min));

                                                s.change(s._validate(v));
                                                s._draw();

                                                // long time keydown speed-up
                                                to = window.setTimeout(function () {
                                                    m *= 2;
                                                }, 30);
                                            }
                                        }
                                    }
                                )
                                .bind(
                                    "keyup",
                                    function (e) {
                                        if (isNaN(kval)) {
                                            if (to) {
                                                window.clearTimeout(to);
                                                to = null;
                                                m = 1;
                                                s.val(s.$.val());
                                            }
                                        } else {
                                            // kval postcond
                                            (s.$.val() > s.o.max && s.$.val(s.o.max))
                                            || (s.$.val() < s.o.min && s.$.val(s.o.min));
                                        }
                                    }
                                );

                            this.$c.bind("mousewheel DOMMouseScroll", mw);
                            this.$.bind("mousewheel DOMMouseScroll", mw);
                        };

                        this.init = function () {
                            if (this.v < this.o.min
                                || this.v > this.o.max) { this.v = this.o.min; }

                            this.$.val(this.v);
                            this.w2 = this.w / 2;
                            this.cursorExt = this.o.cursor / 100;
                            this.xy = this.w2 * this.scale;
                            this.lineWidth = this.xy * this.o.thickness;
                            this.lineCap = this.o.lineCap;
                            this.radius = this.xy - this.lineWidth / 2;

                            this.o.angleOffset
                            && (this.o.angleOffset = isNaN(this.o.angleOffset) ? 0 : this.o.angleOffset);

                            this.o.angleArc
                            && (this.o.angleArc = isNaN(this.o.angleArc) ? this.PI2 : this.o.angleArc);

                            // deg to rad
                            this.angleOffset = this.o.angleOffset * Math.PI / 180;
                            this.angleArc = this.o.angleArc * Math.PI / 180;

                            // compute start and end angles
                            this.startAngle = 1.5 * Math.PI + this.angleOffset;
                            this.endAngle = 1.5 * Math.PI + this.angleOffset + this.angleArc;

                            var s = max(
                                String(Math.abs(this.o.max)).length,
                                String(Math.abs(this.o.min)).length,
                                2
                            ) + 2;

                            this.o.displayInput
                                && this.i.css({
                                        'width' : ((this.w / 2 + 4) >> 0) + 'px',
                                        'height' : ((this.w / 3) >> 0) + 'px',
                                        'position' : 'absolute',
                                        'vertical-align' : 'middle',
                                        'margin-top' : ((this.w / 3) >> 0) + 'px',
                                        'margin-left' : '-' + ((this.w * 3 / 4 + 2) >> 0) + 'px',
                                        'border' : 0,
                                        'background' : 'none',
                                        //'font' : this.o.fontWeight + ' ' + ((this.w / s) >> 0) + 'px ' + this.o.font,
                                        'text-align' : 'center',
                                        'color' : this.o.inputColor || this.o.fgColor,
                                        'padding' : '0px',
                                        '-webkit-appearance': 'none'
                                        }) || this.i.css({
                                            'width': '0px',
                                            'visibility': 'hidden'
                                        });
                        };

                        this.change = function (v) {
                            this.cv = v;
                            this.$.val(this.o.format(v));
                        };

                        this.angle = function (v) {
                            return (v - this.o.min) * this.angleArc / (this.o.max - this.o.min);
                        };

                        this.arc = function (v) {
                          var sa, ea;
                          v = this.angle(v);
                          if (this.o.flip) {
                              sa = this.endAngle + 0.00001;
                              ea = sa - v - 0.00001;
                          } else {
                              sa = this.startAngle - 0.00001;
                              ea = sa + v + 0.00001;
                          }
                          this.o.cursor
                              && (sa = ea - this.cursorExt)
                              && (ea = ea + this.cursorExt);

                          return {
                              s: sa,
                              e: ea,
                              d: this.o.flip && !this.o.cursor
                          };
                        };

                        this.draw = function () {
                            var c = this.g,                 // context
                                a = this.arc(this.cv),      // Arc
                                pa,                         // Previous arc
                                r = 1;

                            c.lineWidth = this.lineWidth;
                            c.lineCap = this.lineCap;

                            if (this.o.bgColor !== "none") {
                                c.beginPath();
                                    c.strokeStyle = this.o.bgColor;
                                    c.arc(this.xy, this.xy, this.radius, this.endAngle - 0.00001, this.startAngle + 0.00001, true);
                                c.stroke();
                            }

                            if (this.o.displayPrevious) {
                                pa = this.arc(this.v);
                                c.beginPath();
                                c.strokeStyle = this.pColor;
                                c.arc(this.xy, this.xy, this.radius, pa.s, pa.e, pa.d);
                                c.stroke();
                                r = this.cv == this.v;
                            }

                            c.beginPath();
                            c.strokeStyle = r ? this.o.fgColor : this.fgColor ;
                            c.arc(this.xy, this.xy, this.radius, a.s, a.e, a.d);
                            c.stroke();
                        };

                        this.cancel = function () {
                            this.val(this.v);
                        };
                    };

                    $.fn.dial = $.fn.knob = function (o) {
                        return this.each(
                            function () {
                                var d = new k.Dial();
                                d.o = o;
                                d.$ = $(this);
                                d.run();
                            }
                        ).parent();
                    };

                }));

                /*!jQuery Circular CountDown*/
                /**
                 * Downward compatible
                 *
                 * Version: 1.0.0 (26/04/2013)
                 * Requires: jQuery v1.7+
                 *
                 * Copyright (c) 2013 Nikhil Navadiya
                 *
                 * Thanks to http://www.javascriptkit.com/
                 */
                (function($) {
                    $.fn.ccountdown = function(_yr, _m, _d, _t, callback) {
                        var $this = this;
                        var interval;
                        var _montharray = new Array("Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec")
                        var _today = new Date();
                        // calling function first time so that it wll setup remaining time
                        var _changeTime = function() {
                            var _today = new Date();
                            var _todayy = _today.getYear();
                            if (_todayy < 1000)
                                _todayy += 1900;
                            var _todaym = _today.getMonth();
                            var _todayd = _today.getDate();
                            var _todayh = _today.getHours();
                            var _todaymin = _today.getMinutes();
                            var _todaysec = _today.getSeconds();
                            _todaysec = "0" + _todaysec;
                            _todaysec = _todaysec.substr(_todaysec.length - 2);
                            var _todaystring = _montharray[_todaym] + " " + _todayd + ", " + _todayy + " " + _todayh + ":" + _todaymin + ":" + _todaysec;
                            var _futurestring = _montharray[_m - 1] + " " + _d + ", " + _yr + " " + _t;
                            /* calculation of remaining days, hrs, min, and secs */
                            _dd = Date.parse(_futurestring) - Date.parse(_todaystring);
                            _dday = Math.floor(_dd / (60 * 60 * 1000 * 24) * 1);
                            _dhour = Math.floor((_dd % (60 * 60 * 1000 * 24)) / (60 * 60 * 1000) * 1);
                            _dmin = Math.floor(((_dd % (60 * 60 * 1000 * 24)) % (60 * 60 * 1000)) / (60 * 1000) * 1);
                            _dsec = Math.floor((((_dd % (60 * 60 * 60 * 1000 * 24)) % (60 * 60 * 1000)) % (60 * 1000)) / 1000 * 1);
                            var el = $($this);
                            var $ss = el.find(".second"), $mm = el.find(".minute"), $hh = el.find(".hour"), $dd = el.find(".days");
                            $ss.val(_dsec).trigger("change");
                            $mm.val(_dmin).trigger("change");
                            $hh.val(_dhour).trigger("change");
                            $dd.val(_dday).trigger("change");
                            if (_dd == 0){
                                window.clearInterval(interval);
                                if (typeof callback == 'function'){
                                    callback.call(this);
                                }
                            }
                        };

                        _changeTime();

                        interval = setInterval(_changeTime, 1000);
                    };
                })(jQuery);
                $(function() {
                    $(".countdown1-countdown .knob").knob({
                        /*change : function (value) {
                            //console.log("change : " + value);
                        },
                        release : function (value) {
                            console.log("release : " + value);
                        },
                        cancel : function () {
                            console.log("cancel : " + this.value);
                        },*/
                        draw : function () {

                            // "tron" case
                            if(this.$.data('skin') == 'tron') {

                                var a = this.angle(this.cv)  // Angle
                                    , sa = this.startAngle          // Previous start angle
                                    , sat = this.startAngle         // Start angle
                                    , ea                            // Previous end angle
                                    , eat = sat + a                 // End angle
                                    , r = true;

                                this.g.lineWidth = this.lineWidth;

                                this.o.cursor
                                    && (sat = eat - 0.3)
                                    && (eat = eat + 0.3);

                                if (this.o.displayPrevious) {
                                    ea = this.startAngle + this.angle(this.value);
                                    this.o.cursor
                                        && (sa = ea + 0.3)
                                        && (ea = ea - 0.3);
                                    this.g.beginPath();
                                    this.g.strokeStyle = this.previousColor;
                                    this.g.arc(this.xy, this.xy, this.radius - this.lineWidth, sa, ea, false);
                                    this.g.stroke();
                                }

                                this.g.beginPath();
                                this.g.strokeStyle = r ? this.o.fgColor : this.fgColor ;
                                this.g.arc(this.xy, this.xy, this.radius - this.lineWidth, sat, eat, false);
                                this.g.stroke();

                                this.g.lineWidth = 2;
                                this.g.beginPath();
                                this.g.strokeStyle = this.o.fgColor;
                                this.g.arc(this.xy, this.xy, this.radius - this.lineWidth + 1 + this.lineWidth * 2 / 3, 0, 2 * Math.PI, false);
                                this.g.stroke();

                                return false;
                            }
                        }
                    });

                    // Example of infinite knob, iPod click wheel
                    var v, up=0,down=0,i=0
                        ,$idir = $("div.idir")
                        ,$ival = $("div.ival")
                        ,incr = function() { i++; $idir.show().html("+").fadeOut(); $ival.html(i); }
                        ,decr = function() { i--; $idir.show().html("-").fadeOut(); $ival.html(i); };
                    $("input.infinite").knob(
                    {
                    min : 0
                    , max : 20
                    , stopper : false
                    , change : function () {
                            if(v > this.cv){
                                if(up){
                                    decr();
                                    up=0;
                                }else{up=1;down=0;}
                            } else {
                                if(v < this.cv){
                                    if(down){
                                        incr();
                                        down=0;
                                    }else{down=1;up=0;}
                                }
                            }
                            v = this.cv;
                        }
                    });
                });

                // Initialize Countdown
                $(".countdown1-countdown").ccountdown(
                    '2020',
                    '1',
                    '6',
                    '08:00:00');
