function resizeSlider_ir3964() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1000");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir3964-ir3964-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir3964-ir3964-slider").style.height = Math.max((parseFloat("500")/parseFloat("1400") * containerWidth), parseFloat("500")) + "px";
	document.getElementById("ir3964-ir3964-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resizeSlider_ir3964(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resizeSlider_ir3964(); });
}
