function ENAutoComplete(opts) {
  if (!opts) return;

  var _this = this;

  var menuHTML = "";

  jQuery(opts.src).each(function() {
    menuHTML += '
' + this.label + '';
  });

  _this.$input = jQuery("#" + opts.inputId);
  _this.$autoComplete = jQuery("#" + opts.inputId + "-en-autocomplete");
  _this.$autoCompleteHelper = jQuery("#" + opts.inputId + "-en-autocomplete-helper");

  _this.$autoComplete.html(menuHTML);

  _this.$input.parent()
  .delegate("#" + opts.inputId, "blur", function(e) {
    _this.hasFocus = false;
  })
  .delegate("#" + _this.$autoComplete.attr("id"), "mousedown", function(e) {
    if (_this.hasFocus) {
      e.stopPropagation();
      e.preventDefault();
      _this.$input.focus();
      return false;
    }
  })
  .delegate('.en-autocomplete li', 'click', function() {
    var $this = jQuery(this);
    document.location.href = opts.href + $this.attr("val") + ($this.attr("version") ? "&fvv=" + $this.attr("version") : "");
  });

  jQuery(document.documentElement).click(function() {
    if (!_this.hasFocus) {
      _this.closeMenu();
    }
  });

  _this.$input.click(function() {
    if (!_this.isOpen) {
      _this.openMenu(this);
    } else {
      _this.closeMenu();
    }
  }).keyup(function(e) {
    _this.searchTerm(this.value);
  }).attr({
    defaultValue: opts.defaultText,
    value: opts.defaultText
  });
}
ENAutoComplete.prototype.isOpen = false;
ENAutoComplete.prototype.hasFocus = false;
ENAutoComplete.prototype.closeMenu = function(inputEle) {
  this.isOpen = false;
  this.$autoComplete.hide();
  this.$autoCompleteHelper.hide();
  if (this.$input.val() == "") {
    this.$input.val(this.$input.attr("defaultValue"));
    this.$input.removeClass("active");
  }
}
ENAutoComplete.prototype.openMenu = function(input) {
  var $input = jQuery(input);
  this.$autoComplete.show();
  if (input.value == $input.attr("defaultValue")) {
    input.value = "";
  }
  this.searchTerm(input.value);
  $input.addClass("active");
  this.hasFocus = true;
  this.isOpen = true;
}
ENAutoComplete.prototype.searchTerm = function(term) {
  if (term != "") {
    this.$autoComplete.find("li").addClass("hidden");
    var $matches = this.$autoComplete.find("li[rel*='" + term + "']");
    $matches.removeClass("hidden").each(function() {
      var $this = jQuery(this);
      if (term.length >= 1) {
        $this.children(1).html($this.attr("alt").replace(new RegExp("(" + term + ")", "i"), "$1"));
      }
    });
    if ($matches.length == 0) {
      this.$autoComplete.hide();
      this.$autoCompleteHelper.show();
    } else {
      this.$autoCompleteHelper.hide();
      this.$autoComplete.show();
    }
  } else {
    this.$autoComplete.find("li").removeClass("hidden").each(function() {
      var $this = jQuery(this);
      $this.children(1).html($this.attr("alt"));
    });
  }
}


function initSchoolSearch() {
  jQuery.get("/schools/map_data.jsp", function(doc) {  
    jQuery("filters filter", doc).each(function(i) {
      var $filter = jQuery(this);
      var id = $filter.attr("id");
      var attrs = $filter.attr("attrs");
      var filterOpts = [];

      if (attrs) {
        $(attrs.split("|")).each(function() {
          var attrName = this;
          var attrIds = {};
          jQuery("markers marker[" + attrName + "]", doc).each(function(j) {
            var $marker = jQuery(this);
            var id = $marker.attr("id");
            var attrValue = $marker.attr(attrName.toString());
            if (!attrIds[attrValue]) {
              attrIds[attrValue] = [];
            }
            attrIds[attrValue].push(id);
          });
          var attrValuesSorted = []
          $.each(attrIds, function(key, value) {
            attrValuesSorted.push(key);
          });          
          attrValuesSorted.sort();

          $.each(attrValuesSorted, function() {
            var key = this;
            var value = attrIds[key];
            var version = $filter.attr(attrName + "version");
            $filter.append($(''));
          });
        })
      }

      $filter.find("group").each(function(j) {
        var $group = jQuery(this);
        var label = $group.attr("key");
        var value = j;
        var item = { label: label, value: value };
        if ($group.attr("version")) {
          item.version = $group.attr("version");
        }
        filterOpts.push(item);
      });
      new ENAutoComplete({ inputId: "filter" + (i + 1),  src: filterOpts, href: "/schools/#fk=" + id + "&fv=", defaultText: "By " + id });
    });

    var schools = [];
    var schoolZips = [];
    jQuery("markers marker", doc).each(function(i) {
      var $this = jQuery(this);
      schools.push({ value: $this.attr("id"), label: $this.attr("name") });
      if (!schoolZips.values) {
        schoolZips.values = ";";
      }
      if (schoolZips.values.indexOf(";" + $this.attr("zip") + ";") == -1) {
        schoolZips.push({ value: $this.attr("zip"), label: $this.attr("zip") });
        schoolZips.values += $this.attr("zip") + ";";
      }
    });

    //new ENAutoComplete({ inputId: "school-filter",  src: schools, href: "/schools/#id=", defaultText: "By Name or Zip", filterSchoolsByZip: false });
  });
}
if (!window.jQuery) {
  window.onload = function() {
    //document.body.innerHTML += ('');
    initSchoolSearch();
  }
} else {
  jQuery(document).ready(function() {
    initSchoolSearch();
  });
}

