                $("#gb-sitename").prepend("
" + siteNameTwo + "");
                $("#gb-footer").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
                $("#gb-footer").prepend("
" + siteNameOne + " " + siteNameTwo + "");
            }
        },

