        "PositionMap": function() {
        	if(this.GetBreakPoint() == "desktop") {
                /* NICE RESOURCE: https://stackoverflow.com/questions/9281320/calculate-new-width-when-skewing-in-canvas */
                var footerHeight = $("#gb-footer-outer").outerHeight(true);
                var mapOffset = Math.tan(30.5 * Math.PI / 180) * footerHeight; /* (Math.tan(radians) * footer height */

                $("#gb-footer-map iframe").css("right", (mapOffset / 2));
                $("#gb-back-to-top").css("right", (410 - mapOffset));
            }
        },

        "Slideshow": function() {
        	$("#gb-page .hp-custom-mmg .ui-widget.app.multimedia-gallery").each(function() {
                var mmg = eval("multimediaGallery" + $(this).attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $(this).csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1240,
                    "imageHeight" : 438,
                    "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : false,
                    "bullets" : true,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 1.5,
                    "transitionDelay" : 6,
                    "fullScreenRotator" : false,
                    "fullScreenBreakpoints" : [], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) { // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                        $(".mmg-description-outer .mmg-description", props.element).children().not($(".mmg-description-links", props.element)).wrapAll('
');

                        var mmgDescriptionHeight = ($(".mmg-description-outer .mmg-description:empty", props.element).length) ? 0 : $(".mmg-description-outer .mmg-description", props.element).outerHeight(true);
                        $(".mmg-description-outer", props.element).css("height", mmgDescriptionHeight);
                    },
                    "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) { // props.element, props.mmgRecords
                        $(".mmg-container", props.element).append($(".mmg-description-outer", props.element));
                        $(".mmg-description-outer .mmg-description", props.element).children().not($(".mmg-description-links", props.element)).wrapAll('
');

                        var mmgDescriptionHeight = ($(".mmg-description-outer .mmg-description:empty", props.element).length) ? 0 : $(".mmg-description-outer .mmg-description", props.element).outerHeight(true);
                        $(".mmg-description-outer", props.element).css("height", mmgDescriptionHeight);
                    },
                    "onWindowResize": function(props) {  // props.element, props.mmgRecords
                        if(!$(".mmg-description-outer .mmg-description .mmg-description-left", props.element).length) {
                            $(".mmg-description-outer .mmg-description", props.element).children().not($(".mmg-description-links", props.element)).wrapAll('
');
                        }

                        var mmgDescriptionHeight = ($(".mmg-description-outer .mmg-description:empty", props.element).length) ? 0 : $(".mmg-description-outer .mmg-description", props.element).outerHeight(true);
                        $(".mmg-description-outer", props.element).css("height", mmgDescriptionHeight);
                    }
                });
            });
        },

        "CheckSlideshow": function() {
        	// FOR SCOPE
            var _this = this;

            if($(".hp").length) {
            	var pmi = "";

            	$("#gb-page .hp-custom-mmg .ui-widget.app.multimedia-gallery").each(function() {
                	pmi = $(this).attr("data-pmi");

                    if ($(window).scrollTop() <= $(this).offset().top + $(this).height() - $("#gb-channel-list-outer").outerHeight(true)) {
                        if(_this.MMGHeightIsFixed[pmi]) {
                        	$(this).removeAttr("style");

                            _this.MMGHeightIsFixed[pmi] = false;
                        }
                    } else {
                    	if(!_this.MMGHeightIsFixed[pmi]) {
                            $(this).css({
                                "height": $(this).height(),
                                "overflow": "hidden"
                            });

                            _this.MMGHeightIsFixed[pmi] = true;
                        }
                    }
                });
            }
        },

