        "Search": function() {
            var _this = this;

            $(document).on("click keydown", "#gb-search-toggle", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    // IF SEARCH IS CLOSED
                    if($("#gb-search").attr("aria-hidden") == "true") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search").attr("aria-hidden", "false");

                        if(e.type == "keydown") {
                        	setTimeout(function() {
                            	$("#gb-search-input").focus();
                            }, 500);
                        }
                    }

                    // IF SEARCH IS OPEN
                    else {
                    	$(this).attr("aria-expanded", "false");
                    	$("#gb-search").attr("aria-hidden", "true");
                    }
                }
            });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "Search for...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search for...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search for...");
                }
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

