function resizeSlider_ir5776() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1100");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir5776-ir5776-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir5776-ir5776-slider").style.height = Math.max((parseFloat("500")/parseFloat("1400") * containerWidth), parseFloat("500")) + "px";
	document.getElementById("ir5776-ir5776-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resizeSlider_ir5776(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resizeSlider_ir5776(); });
}
resizeSlider_ir5776();

div.script:

function tabs(selectedtab) {    
    // contents
    var s_tab_content = "tab_content_" + selectedtab;   
    var contents = document.getElementsByTagName("div");
    for(var x=0; x<contents.length; x++) {
        name = contents[x].getAttribute("name");
        if (name == 'tab_content') {
            if (contents[x].id == s_tab_content) {
            contents[x].style.display = "block";                        
            } else {
            contents[x].style.display = "none";
            }
        }
    }   
    // tabs
    var s_tab = "tab_" + selectedtab;       
    var tabs = document.getElementsByTagName("a");
    for(var x=0; x<tabs.length; x++) {
        name = tabs[x].getAttribute("name");
        if (name == 'tab') {
            if (tabs[x].id == s_tab) {
            tabs[x].className = "active";      
            tabs[x].className = "ir";                 
            } else {
            tabs[x].className = "";
            tabs[x].className = "ir";
            }
        }
    }     
}

// Equalize height 
function equalHeight(parent, children) {
	// Define height 
	var bxSlider = document.querySelectorAll(parent);
	var maxHeight = document.querySelector(children).clientHeight;
	
	// Loop through each LI and add min height
	for (i = 0; i < bxSlider.length; i++) { 
		bxSlider[i].style.minHeight = maxHeight - 2 + "px";
	}// End loop 
}

function adaptiveThumbHeight(parentID) {
	var totalNumThumbs = document.querySelectorAll(parentID + ' .news-thumb');
	for (var i = 0; i < totalNumThumbs.length; i++) { 
		  var thumbContainerHeight = totalNumThumbs[i].offsetHeight; 
		  var thumbImgHeight = totalNumThumbs[i].querySelector('img').height;
		  var totalDiff = (thumbContainerHeight - thumbImgHeight) / 2;
		  
		  if (thumbImgHeight > thumbContainerHeight) {
			  totalNumThumbs[i].className += " tallImage";
		  }
		  
		  if (totalDiff > 0) {
		  	totalNumThumbs[i].querySelector('img').style.marginTop = totalDiff + "px";
		  }
	}
}

