function resizeSlider_ir6443() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1100");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir6443-ir6443-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir6443-ir6443-slider").style.height = Math.max((parseFloat("226")/parseFloat("341") * containerWidth), parseFloat("226")) + "px";
	document.getElementById("ir6443-ir6443-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

