window.twttr = (function(d, s, id) {
	var js, fjs = d.getElementsByTagName(s)[0],
		t = window.twttr || {};
	if (d.getElementById(id)) return t;
	js = d.createElement(s);
	js.id = id;
	js.src = "https://platform.twitter.com/widgets.js";
	fjs.parentNode.insertBefore(js, fjs);
	t._e = [];
	t.ready = function(f) {
		t._e.push(f);
	};
	return t;
}(document, "script", "twitter-wjs"));

(function() {
	var dataSource = {
		sourceType: 'profile',
		screenName: 'cisdpirates'
	};
	var target = document.getElementById('twitter-timeline-cisdpirates');
	var options = {
		chrome: '     ',
		theme: 'light',
		height: '384',
		width: '304',

		linkColor: '#2b7bb9',
		ariaPolite: 'polite'
	};

	function initTwitterTimeline() {
		twttr.ready(function(twttr) {
			twttr.widgets.createTimeline(dataSource, target, options);
		});
	}
	window.addEventListener("load", initTwitterTimeline, false);
})()

