$(".group842880").colorbox({rel:'group842880', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group842880").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery842880 = $("a[rel=group842880]").colorbox({maxWidth: "80%"});
$("a#openGallery842880").click(function(e){
    e.preventDefault();
    $gallery842880.eq(0).click();
});

