div.script:
(function($JssorSlider$) {
	'use strict';

	var maxWidthOrHeight = 
		document.getElementById('edlio_slider_container_13206').clientWidth;

	var maxNumberOfBullets = Math.round(
		maxWidthOrHeight / 35
	);
	var numberOfSlides = 16;

	var options = {
		$FillMode: 2,             
		$AutoPlay: true,          
		$LazyLoading: 1,          
		$AutoPlayInterval: 5000,  
		$SlideDuration: 500,      
		$PauseOnHover: 0,         
		$DragOrientation: 0,      
		$HWA: false,              
		$SlideshowOptions: {
			$Class: $JssorSlideshowRunner$,
			$Transitions: [
				{$Duration:700,$Opacity:2,$Brother:{$Duration:1000,$Opacity:2}}, // twin fade 

			],
			$TransitionsOrder: 0  
		},

		$ArrowNavigatorOptions: {                       
			$Class: $JssorArrowNavigator$,              
			$ChanceToShow: 2,                           
			$AutoCenter: 2,                             
			$Steps: 1                                   
		},
		$ThumbnailNavigatorOptions: {                   
			$Class: $JssorThumbnailNavigator$,          
			$ChanceToShow: 2,                           
			$ActionMode: 1,                             
			$SpacingX: 8,                               
			$DisplayPieces: 8,                          
			$ParkingPosition: 360,                      
			$Orientation: 
					1

		},
		$BulletNavigatorOptions: {                      
			$Class: $JssorBulletNavigator$,             
			$ChanceToShow: 2,                           
			$AutoCenter: 
					1
				,                                
			$Steps: 1,                                  
			$Lanes: Math.ceil(numberOfSlides / maxNumberOfBullets),              
			$SpacingX: 10,                              
			$SpacingY: 10,                              
			$Orientation: 
					1

		},
		$CaptionSliderOptions: {
			$Class: $JssorCaptionSlider$,
			$CaptionTransitions: [{$Duration:700,$Opacity:2,$Brother:{$Duration:1000,$Opacity:2}}],
			$PlayInMode: 1,
			$PlayOutMode: 3
		}

	};
	var edlioJssorSlider = new $JssorSlider$('edlio_slider_container_13206', options);


	function resizeEdlioSlider () {
		var parentWidth = document.getElementById('edlio_slider_container_13206').parentElement.clientWidth;

		if (parentWidth) {
			edlioJssorSlider.$ScaleWidth(parentWidth);
		} else {
			window.setTimeout(resizeEdlioSlider, 30);
		}
	}


		resizeEdlioSlider();







		window.addEventListener("load", resizeEdlioSlider);
		window.addEventListener("resize", resizeEdlioSlider);
		window.addEventListener("orientationchange", resizeEdlioSlider);


})($JssorSlider$);

link.script:

window.onload = init;
var topnavButton = document.getElementById('topnav_mobile_toggle');
var topnavDisplay = document.getElementById('topnav');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}

window.addEventListener('scroll', function(event) {
    var toggleElement = document.getElementById('header_main');
    var watchHeight = 100;

    if ( window.pageYOffset >= watchHeight ) {
        toggleElement.classList.add('shrink');
        return;
    } else {
        toggleElement.classList.remove('shrink');
        return;
    }
});
