meta.script:

$(document).ready(function () {
	linkLibrary();
	$('#sw-content-layout-wrapper').ajaxComplete(function( event, xhr, settings ) {
		if($('.app.link-library').length > 0){
			linkLibrary(); 
		}
	});
});
function linkLibrary(){
	$( '.sw-links-link').hide();
	$( '.link-library .ui-article' ).each(function(appIndex, app) {
		var theLink = $( '.sw-links-link a',app ).attr( 'href' );
        if(!$(".title-link",app).length){
			$('h1.ui-article-title ', this).wrap('' );
		}
    });
};

