            // MEGA MENU ACCESSIBILITY         
                $(".mega-channel ul.sw-channel-dropdown").each(function() {
                    var sectionLink = $("a", this);
                    var firstSectionLinkIndex = $(sectionLink).index($(sectionLink).first());
                    var firstSectionLink = $(sectionLink).eq($(sectionLink).index($(sectionLink).first()));
                    var lastSectionLinkIndex = $(sectionLink).index($(sectionLink).last());
                    var lastSectionLink = $(sectionLink).eq($(sectionLink).index($(sectionLink).last()));

                    $(sectionLink).keydown(function(e) {
                        var thisIndex = sectionLink.index(this);
                        var newIndex = -1;

                        switch(e.keyCode) {
                            // CONSUME UP ARROW
                            case _this.KeyCodes.up:
                                e.preventDefault();

                                // IS FIRST ITEM
                                if($(this).is(firstSectionLink)) {
                                    $(this).closest(".mega-channel ul.sw-channel-dropdown").find("a").attr("tabindex", "-1");
                                    $(this).closest(".sw-channel-item").find("> a").focus();
                                }

                                // FOCUS PREVIOUS ITEM
                                else {
                                    $(this).closest(".mega-channel ul.sw-channel-dropdown").find("a").attr("tabindex", "-1");
                                    sectionLink.eq(thisIndex - 1).attr("tabindex", "0").focus();
                                }
                            break;

                            // CONSUME DOWN ARROW
                            case _this.KeyCodes.down:
                                e.preventDefault();

                                // IS LAST ITEM
                                if($(this).is(lastSectionLink)) {
                                    $(this).closest(".mega-channel ul.sw-channel-dropdown").find("a").attr("tabindex", "-1");
                                    sectionLink.eq(firstSectionLinkIndex).attr("tabindex", "0").focus();
                                }

                                // FOCUS NEXT ITEM
                                else {
                                    $(this).closest(".mega-channel ul.sw-channel-dropdown").find("a").attr("tabindex", "-1");
                                    sectionLink.eq(thisIndex + 1).attr("tabindex", "0").focus();
                                }
                            break;

                            // CONSUME LEFT ARROW
                            case _this.KeyCodes.left:
                                e.preventDefault();

                                if($(this).closest(".sw-channel-item").is(":first-child")) {
                                    $(this).closest("#gb-mega-menu").find(".sw-channel-item:first-child > a").focus();
                                } else {
                                    $(this).closest(".sw-channel-item").prev(".sw-channel-item").find("> a").focus();
                                }
                            break;

                            // CONSUME RIGHT ARROW
                            case _this.KeyCodes.right:
                            case _this.KeyCodes.tab:
                                e.preventDefault();

                                if($(this).closest(".sw-channel-item").is(":last-child")) {
                                    $(this).closest("#gb-mega-menu").find(".sw-channel-item:last-child > a").focus();
                                } else {
                                    $(this).closest(".sw-channel-item").next(".sw-channel-item").find("> a").focus();
                                }
                            break;

                            // CONSUME HOME KEY
                            case _this.KeyCodes.home:
                                e.preventDefault();

                                // FOCUS FIRST ITEM
                                $(this).closest(".mega-channel ul.sw-channel-dropdown").find("a").attr("tabindex", "-1");
                                $(this).closest(".mega-channel ul.sw-channel-dropdown").find("li:first-child > a").attr("tabindex", "0").focus();
                            break;

                            // CONSUME END KEY
                            case _this.KeyCodes.end:
                                e.preventDefault();

                                // FOCUS LAST ITEM
                                $(this).closest(".mega-channel ul.sw-channel-dropdown").find("a").attr("tabindex", "-1");
                                $(this).closest(".mega-channel ul.sw-channel-dropdown").find("li:last-child > a").attr("tabindex", "0").focus();
                            break;

                            // CONSUME ESC KEY
                            case _this.KeyCodes.esc:
                                e.preventDefault();

                                // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                                $(this).closest(".sw-channel-item").find("> a").focus();
                                $(this).closest(".mega-channel ul.sw-channel-dropdown").find("a").attr("tabindex", "-1");
                                $(this).closest(".sw-channel-item").find("> a").attr("aria-expanded", "false").parent().removeClass("hover").find(".gb-sections").attr("aria-hidden", "true").slideUp(300, "swing");
                            break;

                            // CONSUME SPACE KEY
                            case _this.KeyCodes.space:
                                e.preventDefault();

                                $(this).click();
                            break;

                            // CONSUME SHIFT+TAB
                            case _this.KeyCodes.tab:
                                if(e.shiftKey) {
                                    e.preventDefault();

                                    if($(this).closest(".sw-channel-item").is(":first-child")) {
                                        $(this).closest("#gb-mega-menu").find(".sw-channel-item:first-child > a").focus();
                                    } else {
                                        $(this).closest(".sw-channel-item").prev(".sw-channel-item").find("> a").focus();
                                    }
                                }
                            break;
                        }
                    });
                });

                // MOVE LAST 4 CHANNEL ITEMS
                    $('#gb-header-outer .gb-wrap').append('');
                    $('#channel-navigation > li:not(.hidden-channel)').slice(-4).appendTo('#gb-header-outer .gb-wrap nav ul');
                    $('#header-nav > ul > li:eq(0) > a').prepend('')
                    $('#header-nav > ul > li:eq(1) > a').prepend('');
                    $('#header-nav > ul > li:eq(2) > a').prepend('');
                    $('#header-nav > ul > li:eq(3) > a').prepend('');

        },

        // ADJUST CHANNEL DROPDOWN POSITION
        "AdjustChannelDropdown":function() {

          		var windowWidth = $(window).width();
                var channelListTopOffset = 0;
                var channelRightOffset = 0;
                var diff = 0;

                $("#channel-navigation .sw-channel-item:not(.mega-channel), #header-nav .sw-channel-item:not(.mega-channel)").each(function() {
					channelRightOffset = $(this).offset().left + $(this).outerWidth(true);
                    diff = windowWidth - channelRightOffset;
                    if(diff < 300) { // 300 IS THE WIDTH OF THE DROPDOWN
                    	$(this).addClass("set-dropdown-right");
                    }
                });
        },



        "Body": function() {
        var _thi =this;
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");


            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }


        },

        "Facts": function() {

        if(true === true) {
        	$('#facts').css('display', 'block');

            $("#facts-wrapper .fact").each(function() {
            	var iconSrc = $(".icon", this).attr("data-icon");
                var iconType = ((iconSrc.split("/")).indexOf("cms") > -1) ? "custom" : "library";

                if(iconType == "custom") {
                	$(".icon", this).html('');
                } else {
                	var iconSelection = iconSrc.split("/");
                    var iconCategory = iconSelection[0].toLowerCase().replace(/\s+/g, "-");
                    var icon = (iconSelection[1].split("."))[0];
                    var finalIcon = "sw-icon-" + iconCategory + "-" + icon;

                	$(".icon", this).addClass(finalIcon);
                }

            });

        }else {
        	$('#facts').remove();
        }



        },

        "SocialTabs": function() {
