            if($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                /*$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 960,
                    "imageHeight" : 500,
                    "mobileDescriptionContainer": [640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [], // ["image", "title", "overlay"]
                    "playPauseControl" : false,
                    "backNextControls" : false,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 1.5,
                    "transitionDelay" : 4,
                    "fullScreenRotator" : false,
                    "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {} // props.element, props.mmgRecords
                });
            }*/

            var mmgRecords = "";

                // REMOVE LAST EMPTY RECORD OBJECT TO KEEP THINGS TIDY
                mmg.records.splice(-1, 1);

                // START THE ARTICLE STRUCTURE
                mmgRecords = '';

                // REMOVE MMG-CONTAINER TO KEEP THINGS CLEAN
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .mmg-container").remove();

                // LOOP THROUGH AND BUILD THE ARTICLES




                $.each(mmg.records, function(index, record) {
                    mmgRecords +=	'
' +
                                        '
' +
                                            '
' +
                                                /*'' +*/
                                                //((record.videoIsEmbedded != "False") ? 'Watch Video' : '') +
                                            '' +
                                            '
' +
                                            	((record.hideTitle == "False") ? '
' + record.title + ' ' : '') +
                                                '
' +
                                                    ((record.hideCaption == "False") ? '
' + record.caption + '' : '') +
                                                '
' +
                                                    ((record.isLinked != "False") ? '
' + record.linkText + '' : '') +
                                                    ((record.videoIsEmbedded != "False") ? 'Watch Video' : '') +
                                                '' +
                                                '' +
                                             '' +
                                        '' +
                                    '';


                });

                // END THE ARTICLE STRUCTURE
                mmgRecords += '';

                // ADD THE ARTICLES TO THE DOM
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .ui-widget-detail").append(mmgRecords);


                // BIND EVENTS
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").on("click keydown", ".article-video-button", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        // GET ACTIVE RECORD INDEX
                        var recordIndex = $(this).attr("data-record-index");

                        function decodeIframeHtml(iframe) {
                            var textArea = document.createElement("textarea");
                            textArea.innerHTML = iframe;

                            return textArea.value;
                        }

                        function addUrlParameters(iframe) {
                            var addedParameters = "";

                            // YOUTUBE EMBED
                            if(iframe.indexOf("youtube.com/embed") > -1) {
                                if(iframe.indexOf("rel=") == -1) {
                                    addedParameters += "&rel=0";
                                }
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=1";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/youtube.com\/embed\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/youtube.com\/embed\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            // VIMEO EMBED
                            if(iframe.indexOf("vimeo.com/video/") > -1) {
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=1";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/vimeo.com\/video\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/vimeo.com\/video\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            // TEACHERTUBE EMBED
                            if(iframe.indexOf("teachertube.com/embed/video/") > -1) {
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=true";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/teachertube.com\/embed\/video\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/teachertube.com\/embed\/video\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            return iframe;
                        }

                        // ADD VIDEO DIALOG
                        $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").append('' +
                            '
' +
                                '
' +
                                    '
' +
                                        '


' +
                                        addUrlParameters(decodeIframeHtml(mmg.records[recordIndex].videoIframe)) +
                                    '' +
                                    'Close' +
                                '' +
                            '' +
                        '');

                        // FOCUS CLOSE BUTTON
                        $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .mmg-video-close").focus();
                    }
                });

                // CLOSE VIDEO
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").on("click keydown", ".mmg-video-close", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        var returnFocusIndex = $(this).attr("data-return-index");

                        // ANIMATE THE VIDEO OUT
                        $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .mmg-video-window").addClass("close-video");

                        setTimeout(function() {
                            // REMOVE THE VIDEO CONTAINER COMPLETELY
                            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .mmg-video-window").remove();

                            // FOCUS WATCH VIDEO LINK
                            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .article-video-button[data-record-index='" + returnFocusIndex + "']").focus();
                        }, 1000);
                    }

                    if(e.type == "keydown" && e.keyCode == _this.KeyCodes.tab) {
                        e.preventDefault();

                        var videoIframe = $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .mmg-video-viewer iframe")[0];
                        videoIframe.contentWindow.focus();
                    }
                });

                // CAPTURE VIDEO WINDOW FOCUS OUT
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").on("focusout", ".mmg-video-window", function() {
                    $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .mmg-video-close").focus();
                });

                // BUILD SLIDER
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csArticleSlider({
                    "viewerNum": [3, 1, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "transitionSpeed": .5, // SECONDS
                    "slideFullView": false,
                    "extend": function(config, element, ArticleSlider) {}
                });

                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first .cs-article-slider-notifications").remove();

                // MOVE/CREATE BUTTONS
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider-control.back").insertBefore($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider-control.next"));
                $('').insertBefore($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider-control.next"));
            	$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider").children().not($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-viewer")).wrapAll('
');

            	// SET PLAY/PAUSE CLASS
                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider").addClass("playing");

                // START AUTO-ROTATION
                var galleryTimer = "";
                sliderTimer();

                // PLAY/PAUSE CLICK
                $(document).on("click keydown", "#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider-control-play-pause", function(e) {
                	if(_this.AllyClick(e)) {
                    	e.preventDefault();

                        // GALLERY IS PAUSED
                        if($(this).hasClass("paused")) {
                        	$(this).removeClass("paused").attr('aria-label', 'Pause Gallery');
                            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider").removeClass("paused").addClass("playing");

                            sliderTimer();
                        } 

                        // GALLERY IS NOT PAUSED
                        else {
                        	$(this).addClass("paused").attr('aria-label', 'Play Gallery');;
                            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider").removeClass("playing").addClass("paused");

                            killSliderTimer();
                        }
                    }
                });

                // MOUSEENTER, MOUSEOUT, FOCUSIN, FOCUSOUT
                $(document).on("mouseenter focusin", "#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider", function(e) {
                	$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider").removeClass("playing").addClass("paused");

                    killSliderTimer();
                }).on("mouseleave focusout", "#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider", function(e) {
                    if(!$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider-control-play-pause").hasClass("paused")) {
                    	$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider").removeClass("paused").addClass("playing");

                        sliderTimer();
					}
                });
            }

            function sliderTimer() {
                var transitionDelay = 6;

                killSliderTimer();

                galleryTimer = setInterval(function() {
                    $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .cs-article-slider-control.next").trigger("click");
                }, (transitionDelay * 1000));
            }

            function killSliderTimer() {
                clearInterval(galleryTimer);
            }

            function hexToRgb(h){
                var r = parseInt((cutHex(h)).substring(0,2),16);
                var g = parseInt((cutHex(h)).substring(2,4),16);
                var b = parseInt((cutHex(h)).substring(4,6),16);
                return r + ', ' + g + ', ' + b;
            }

            function cutHex(h) {
                return (h.charAt(0)=="#") ? h.substring(1,7):h;
            }

            // ADD/REMOVE HOVER CLASS FOR HOVER/FOCUS/TOUCH
            $(document).on("mouseenter focusin touchstart", "#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .ui-article", function(e) {
            	$(this).addClass("hover");
            }).on("mouseleave focusout touchend", "#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery .ui-article", function(e) {
            	$(this).removeClass("hover");
            });
        },

