        "PopulateSchoolList": function() {
        	var _this = this;

        	var elemSchools = "", middleSchools = "", highSchools = "", alternateSchools = "";
            $(".sw-mystart-dropdown.schoollist .sw-dropdown-list > li").each(function() {
                var schoolName = $.trim($("a", this).text()).toLowerCase();


                // CHECK FOR ELEMENTARY
                if(schoolName.indexOf("(es)") > -1) {
                    elemSchools += '
' + $(this).html().replace('(es)', '') + '';
                }

                // CHECK FOR MIDDLE
                else if(schoolName.indexOf("(ms)") > -1) {
                    middleSchools += '
' + $(this).html().replace('(ms)', '') + '';

                }

                // CHECK FOR HIGH
                else if(schoolName.indexOf("(hs)") > -1) {
                    highSchools += '
' + $(this).html().replace('(hs)', '') + '';

                }

                // ALTERNATE
                else if(schoolName.indexOf("(alt)") > -1) {
                    alternateSchools += '
' + $(this).html().replace('(alt)', '') + '';
                }

                $("#elementary").html('
Elementary Schools' + elemSchools + '');
                $("#middle").html('
Middle Schools' + middleSchools + '');
                $("#high").html('
High Schools' + highSchools + '');
                $("#additional").html('
Additional Campuses' + alternateSchools + '');
            });

            $('.rs-menu-group.schools .rs-menu-group-list li a').each(function() {
            	var schools = $(this).text();
                if(schools.indexOf("(es)") != -1 || (schools.indexOf("(ms)") != -1) || (schools.indexOf("(hs)") != -1) || (schools.indexOf("(alt)") != -1)) {
                	$(this).parent().show();
                }else {
                	$(this).parent().remove();
                }



            });




            $('#gb-school-list').append('
' +
        									'' +
        								'');


        },


        "Schools":function() {
        	var _this = this;
            $('#top-bar .schools li a').attr('tabindex', -1);

            //OPEN SCHOOL LIST
        	$('.schools .cs-selector').on('click keydown', function(e) {

            	if(_this.AllyClick(e)) {
                  	e.preventDefault();

                    if($(this).attr('aria-expanded') == "false") {
                        	$(this).attr('aria-expanded',true).addClass('spin');
                            $('#close-schools').attr('tabindex', 0).attr('aria-hidden', false);
							$('.schools .cs-dropdown').attr('aria-hidden', 'false').addClass('open');
                            $('#top-bar .schools li a').attr('tabindex', 0);

                            setTimeout(function() {
                            	$('#top-bar .schools li:first').find('>a').focus();
                            }, 100);
            			}else {
                        	$(this).attr('aria-expanded',false).focus().removeClass('spin');
                            $('#close-schools').attr('tabindex', -1).attr('aria-hidden', true);
                            $('.schools .cs-dropdown').attr('aria-hidden', 'true').removeClass('open');
                            $('#top-bar .schools li a').attr('tabindex', -1);
                        }
                }
            });

            //CLOSE SCHOOL LIST
            $('#close-schools').on('click keydown', function(e) {
            	if(_this.AllyClick(e)) {
                  	e.preventDefault();

                    $('.schools .cs-selector').attr('aria-expanded',false).focus().removeClass('spin');;
                    $('#close-schools').attr('tabindex', -1).attr('aria-hidden', true);
                    $('.schools .cs-dropdown').attr('aria-hidden', 'true').removeClass('open');
                    $('#top-bar .schools li a').attr('tabindex', -1);
                }



            });

            $(document).on("keydown", function(e) {
                setTimeout(function() {
                    if(!$(".cs-mystart-dropdown.schools").find(":focus").length) {
						$('.schools .cs-selector').attr('aria-expanded',false).removeClass('spin');;
                        $('#close-schools').attr('tabindex', -1).attr('aria-hidden', true);
                        $('.schools .cs-dropdown').attr('aria-hidden', 'true').removeClass('open');
                        $('#top-bar .schools li a').attr('tabindex', -1);
                    }
                }, 400);
            });


            //NAVIGATE SCHOOLS
            var _this = this;
            $('.schools a').keydown(function(e){
            	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            	switch(e.keyCode) {

                	// CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        if($(this).closest('div').attr("id") == "elementary" && $(this).parent().is(":first-child")) {
                        	$("#additional li:last-child a").focus();
                        } else if($(this).parent().is(":first-child")) {
                        	$(this).closest('div').prev().find("li:last-child > a").focus();
                        } else {
                            $(this).parent().prev().find("> a").attr("tabindex", "0").focus();
                        }

                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

						if($(this).closest('div').attr("id") == "additional" && $(this).parent().is(":last-child")) {
                        	$("#elementary li:first-child a").focus();
                        } else if($(this).parent().is(":last-child")) {
                        	$(this).closest('div').next().find("li:first-child > a").focus();
                        } else {
                            $(this).parent().next().find("> a").attr("tabindex", "0").focus();
                        }

                    break;

                    // CONSUME ESC KEY
                    case csKeyCodes.esc:
                     e.preventDefault();
                    	$('.schools .cs-selector').attr('aria-expanded',false).focus().removeClass('spin');;
                        $('#close-schools').attr('tabindex', "-1").attr('aria-hidden', true);
                        $('.schools .cs-dropdown').attr('aria-hidden', 'true').removeClass('open');
                        $('#top-bar .schools li a').attr('tabindex', "-1");


                    break;

                    case csKeyCodes.tab:
                      if(!e.shiftKey) {
                            e.preventDefault();
                            $('#close-schools').attr('tabindex', "0").attr('aria-hidden', false).focus();
                            $('#top-bar .schools li a').attr('tabindex', "-1");
                            $("#elementary li:first-child a").attr('tabindex', "0");
                        }

                        if(e.shiftKey) {
                            e.preventDefault();
                            $('.schools .cs-selector').focus();
                            $('#top-bar .schools li a').attr('tabindex', "-1");
                            $("#elementary li:first-child a").attr('tabindex', "0");
                        }
                    break;
                }

           });
        },


