        "Search": function() {
        	var _this = this;
            // //DON"T ALLOW USER TO SUBMIT AN EMPTY FORM
            $('#sw-search-input').off();
            $('#gb-search-input').on('keydown', function(e){
                if(e.which == 13){//Enter key pressed
                    e.preventDefault();
                     if($.trim($("#gb-search-input").val()) != "Search..." && $.trim($("#gb-search-input").val()) != "") {


                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                    }

                } 


            });

            $("#sw-search-button").off().removeAttr("onclick").attr("href", "#");
            $("#gb-search-submit").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();

                    if($.trim($("#gb-search-input").val()) != "Search..." && $.trim($("#gb-search-input").val()) != "") {
                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                    }
                }


            });

            //SEARCH TEXT
            $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder").attr("value", "Search...");

            $("div#sw-mystart-search input#sw-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });


            //OPEN SEARCH FORM
            $('#search').on('click keydown', function(e) {
             	if(_this.AllyClick(e)) {
                  		e.preventDefault();

                        if($(this).attr('aria-expanded') == "false") {
                        	$(this).attr('aria-expanded',true);
             				$('#gb-search-btn').attr('aria-hidden', true);
                            $('#close').attr('tabindex', 0).attr('aria-hidden', false);
                            $('#gb-search-form-bg').addClass('open');
                            $('#gb-search-input').addClass('open').attr('tabindex', 0);
                            $("#gb-search-input").val("");
                            $('#gb-search-submit').attr('tabindex', 0);

                            setTimeout(function() {
                            	$('#gb-search-input').focus();
                                $('#gb-search-submit').fadeIn('slow');
                            }, 100);
            			}
                    }


            });

            //CLOSE SEARCH FORM
            $('#gb-close-search').on('click keydown', function(e) {
            	if(_this.AllyClick(e)) {
            		e.preventDefault();

                    $('#search').attr('aria-expanded',false);
                    $('#gb-search-btn').attr('aria-hidden', false);
                    $('#gb-search-form-bg').removeClass('open');
                    $('#gb-search-input').addClass('open').attr('tabindex', -1);
                    $("#gb-search-input").val("Search...");
                    $('#gb-search-submit').attr('tabindex', -1);
                    $('#close').attr('tabindex', -1);
                    $('#search').focus();
                }
            });


            //KEYBOARD ACCESSIBILITY FOR SEARCH FORM
            $('#gb-search-input').on('keydown', function(e) {
            	if(e.which === 9 && e.shiftKey) {
                	e.preventDefault();
                    $('#gb-close-search').focus();
                }
            });

            $('#gb-close-search').on('keydown', function(e) {
            	if(e.which === 9) {
                	e.preventDefault();
                    $('#gb-search-input').focus();
                }
            });


        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

