function executeQuery() {
    var input = document.getElementById('cse-search-input-box-id');
    var ezbutton = document.getElementById('ezSearchbutton');
    var googleSearch = function (value) {
        console.log(value);
        var url = "https://www.google.com/search?q=site%3Awww.lagovistaisd.net+" + value;
        window.open(url);
    }
    input.onkeyup = function(e) {
        if (e.keyCode == 13) {
            e.preventDefault();
            googleSearch(input.value);
        }
    }
    input.onkeydown = function(e) {
        if (e.keyCode == 13) {
            e.preventDefault();
        }
    }
    input.onkeypress = function(e) {
        if (e.keyCode == 13) {
            e.preventDefault();
        }
    }
    ezbutton.onclick = function() {
        googleSearch(input.value);
    }
    return false;
}

