$(".group867333").colorbox({rel:'group867333', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group867333").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery867333 = $("a[rel=group867333]").colorbox({maxWidth: "80%"});
$("a#openGallery867333").click(function(e){
    e.preventDefault();
    $gallery867333.eq(0).click();
});

