body.script:
if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
	$('body').removeClass('desktop');
}
$(document).ready(function() {

	$(window).on('scroll', function() {
		var delay = 0;
		$('.main .col-item').children().each(function(i) {

			var bottom_of_object = $(this).offset().top;
			var bottom_of_window = $(window).scrollTop() + $(window).height();
			if (bottom_of_window > bottom_of_object) {

				var $li = $(this);
				setTimeout(function() {
					$li.addClass('active');
				}, delay += 30);
			}
		});
	});
	$(window).trigger('scroll');


	$('.slider-container').bxSlider({
		mode: 'fade',
		auto: true,
		autoHover: true,
		adaptiveHeight: true,
		onSlideAfter: function() {
			$('.bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');
		}

	});
});

var slider,
	oBxSettings = {
		auto: true,
		autoHover: true,
		adaptiveHeight: true,
		slideWidth: 733,
		slideMargin: 20,
			pager: false,
			nextText: 'Next News Headline',
			prevText: 'Previous News Headline'
	};

function init() {
	oBxSettings.maxSlides = window.outerWidth < 800 ? 1 : 2;
	oBxSettings.minSlides = window.outerWidth < 800 ? 1 : 2;
	oBxSettings.slideMargin = window.outerWidth < 800 ? 0 : 20;
	oBxSettings.mode = window.outerWidth < 800 ? 'fade' : 'horizontal';
}

$(document).ready(function() {
	init();
	slider = $('.news-slider').bxSlider(oBxSettings);
});

$(window).resize(function() {
	if ((window.outerWidth < 800 && window.prevWidth >= 800) || (window.outerWidth >= 800 && window.prevWidth < 800)) {
		init();
		slider.reloadSlider(oBxSettings);
	}
	window.prevWidth = window.outerWidth;
});
