            $(document).on("keydown", ".mega-channel-cat-outer a", function(e) { 
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();
                        if($(this).parents(".mega-channel-cat-outer").find("a").index(this) == 0 && !$(this).parents(".mega-columns").find(".mega-channel-text a").length) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else if($(this).parents(".mega-columns").find(".mega-channel-text a").length) {
                        	$(this).parents(".mega-columns").find(".mega-channel-text a").focus();
                        } else {
                        	var index = $(this).parents(".mega-channel-cat-outer").find("a").index(this)-1;
                        	$(this).parents(".mega-channel-cat-outer").find("a").eq(index).focus();
                        }
                    break;      
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(this).parents(".mega-channel-cat-outer").find("a").index(this) == $(this).parents(".mega-channel-cat-outer").find("a").length-1) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $(this).parents(".mega-channel-cat-outer").find("a").index(this)+1;
                            $(this).parents(".mega-channel-cat-outer").find("a").eq(index).focus();
                        }
                    break;
                    case _this.KeyCodes.tab:
                   		e.preventDefault();
                        var index = $(this).parents(".mega-channel-cat-outer").find("a").index(this)+1;
                        $(this).parents(".mega-channel-cat-outer").find("a").eq(index).focus();
                    break;
                  }
            });
         },

