  var _gaq = _gaq || [];
  _gaq.push(['_setAccount', 'UA-25413029-1']);
  _gaq.push(['_trackPageview']);

  (function() {
    var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;
    ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';
    var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);
  })();

$(function(){
    var iFrames = $('iframe');
	function iResize() {
		for (var i = 0, j = iFrames.length; i < j; i++) {
		  iFrames[i].style.height = iFrames[i].contentWindow.document.body.offsetHeight + 'px';}
	    }
    	if ($.browser.safari || $.browser.opera) { 
    	   iFrames.load(function(){
    	       setTimeout(iResize, 0);
           });
    	   for (var i = 0, j = iFrames.length; i < j; i++) {
    			var iSource = iFrames[i].src;
    			iFrames[i].src = '';
    			iFrames[i].src = iSource;
           }
    	} else {
    	   iFrames.load(function() { 
    	       this.style.height = this.contentWindow.document.body.offsetHeight + 'px';
    	   });
    	}
    });

body.script:
$(document).ready(init);
function init() {
	var ie = navigator.userAgent.indexOf("MSIE") != -1;
	var ie6 = navigator.userAgent.indexOf("MSIE 6") != -1 || navigator.userAgent.indexOf("MSIE 5") != -1;
	$('#features').cycle({ 
	    fx:     'scrollLeft', 
	    speed:  500, 
	    timeout: 0, 
	    next:   '.feature_next',
	    prev:   '.feature_back' 
	});
	var fadeOutImgChildren = function(){
		 $(this).children('img').fadeOut('normal'); 
	}
	var hideImgChildren = function(){ $(this).children('img').hide(); }
	$("#feature_back_lg").hover(
		function(){ $(this).children('img').show(); },
		ie ? hideImgChildren : fadeOutImgChildren
	);
	$("#feature_next_lg").hover(
		function(){ $(this).children('img').show(); },
		ie ? hideImgChildren : fadeOutImgChildren
	);
}
