function mysearch() {
    var v = document.getElementById("txtSearch").value;
    if (v.trim().length > 0){
        document.location.href = "/page/dis-search?search=" + v.replace("/", " ");
    }
}
function do_search_click(e){
    e.preventDefault();
    mysearch();
}
function do_search_keyup(e) {
    if (e.keyCode == 13) {
        mysearch();
        e.preventDefault();
    }
}

function do_search_key(e) {
    if (e.keyCode == 13) {
        e.preventDefault();
    }
}

