div.script:
document.addEventListener('DOMContentLoaded', function() {

	var shuffle_slick_options = {
		"autoplay": true,
		"autoplaySpeed": 6000,

		"arrows": true,
		"dots": false,
		"dotsClass": "slick-dots slick-dots-bottom",


		"fade": false
	}

	$('#shuffle_main .shuffle-main').slick(shuffle_slick_options);

});

