	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
		"ShowFullSiteButton": false,
        "ChannelBarIsSticky": false,
        "SlideshowDescFixed": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;


            this.SetTemplateProps();
            this.SchoolList();
            this.Translate();
			this.MyStart();
            this.JsMediaQueries();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Slideshow();
            this.CheckSlideshow();
            this.StreamingVideo();
            this.Body();
            this.Shortcuts();
            this.TabbedApps();
            this.PageNavColumns();
            this.ModEvents();
            if($(".hp").length && true == true) {
            	this.IconCollage();
            }
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();
            this.SocialIcons();
            this.ViewFullSite();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			csGlobalJs.OpenInNewWindowWarning();
            $("li.sw-channel-item").hover(function() {
            	$("ul.sw-channel-dropdown").stop( true, true );
            });
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CheckSlideshow();
            this.Shortcuts();
            this.StickyChannelBar();
        },

        "WindowScroll": function() {
            this.StickyChannelBar();
            this.CheckSlideshow();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
					if($("#gb-channel-list-outer #sw-mystart-mypasskey").length) {
                        $("#gb-mystart").append($("#gb-channel-list-outer #sw-mystart-mypasskey"));
                    }

                    var getWindowHeight = $(window).height();
                    var getChannelHeight = $("#gb-channel-list").height();
                    if(getChannelHeight > getWindowHeight) {
                    	$(".gb-page.left").addClass("channel-fallback").css("height",getWindowHeight);
                    } else {
                    	$(".gb-page.left").removeClass("channel-fallback").removeAttr("style");
                    }
                break;
                case "768":
					if($("#gb-mystart #sw-mystart-mypasskey").length) {
                        $("#gb-channel-list-outer").append($("#gb-mystart #sw-mystart-mypasskey"));
                    }

                    if(!$("#sw-mystart-mypasskey").length) {
                    	$("#gb-page").addClass("no-passkey");
                    }
                    if($(".gb-page.left").hasClass("channel-fallback")) {
                    	$(".gb-page.left").removeClass("channel-fallback").removeAttr("style");
                    }
                break;
                case "640":
					if($("#gb-mystart #sw-mystart-mypasskey").length) {
                        $("#gb-channel-list-outer").append($("#gb-mystart #sw-mystart-mypasskey"));
                    }

                    if(!$("#sw-mystart-mypasskey").length) {
                    	$("#gb-page").addClass("no-passkey");
                    }
                    if($(".gb-page.left").hasClass("channel-fallback")) {
                    	$(".gb-page.left").removeClass("channel-fallback").removeAttr("style");
                    }
                break;
                case "480":
					if($("#gb-mystart #sw-mystart-mypasskey").length) {
                        $("#gb-channel-list-outer").append($("#gb-mystart #sw-mystart-mypasskey"));
                    }

                    if(!$("#sw-mystart-mypasskey").length) {
                    	$("#gb-page").addClass("no-passkey");
                    }
                    if($(".gb-page.left").hasClass("channel-fallback")) {
                    	$(".gb-page.left").removeClass("channel-fallback").removeAttr("style");
                    }
                break;
                case "320":
					if($("#gb-mystart #sw-mystart-mypasskey").length) {
                        $("#gb-channel-list-outer").append($("#gb-mystart #sw-mystart-mypasskey"));
                    }

                    if(!$("#sw-mystart-mypasskey").length) {
                    	$("#gb-page").addClass("no-passkey");
                    }
                    if($(".gb-page.left").hasClass("channel-fallback")) {
                    	$(".gb-page.left").removeClass("channel-fallback").removeAttr("style");
                    }
                break;
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

			// ADD DISTRICT HOME
            if(this.ShowDistrictHome && $(".sw-mystart-button.home").length) {
            	$(".sw-mystart-button.home > a > span").text("District Home");
            	$("#gb-mystart").prepend($(".sw-mystart-button.home"));
            }

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

            var mystartLength = $("#gb-mystart > div").length;
            $("#gb-mystart-outer").attr("data-mystart-length",mystartLength);

            $("#gb-mystart > div").each(function() {
            	var thisItem = this;
                var thisItemIndex = $(thisItem).index();
                $(thisItem).attr("data-item-pos",thisItemIndex);
            });

            $("#gb-mystart .cs-mystart-dropdown").each(function() {
            	var thisDropdown = $(this).find(".cs-dropdown");
                var thisSelector = $(this).find(".cs-selector").text();
                $('').appendTo(thisDropdown);
            });

            $(".mystart-dropdown-close").on("click keydown", function(e) {
                var thisCloseBtn = this;
                if(_this.AllyClick(e)) {
                        $(thisCloseBtn).parent().attr("aria-hidden","true").slideUp(300, "swing"); 
                        $(thisCloseBtn).parent().prev().attr("aria-expanded","false").focus();
                        $(thisCloseBtn).parent().parent().removeClass("open");
                }
            });
        },

		"SchoolList": function() {
			// ADD SCHOOL LIST
			if(this.ShowSchoolList) {
				var schoolDropdown =	'
' +
											'
Our Schools' +
											'
' +
												'' + $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html() + '' +
											'' +
										'';

				// ADD SCHOOL LIST TO THE DOM	
				$("#gb-mystart").prepend(schoolDropdown);

				// BIND DROPDOWN EVENTS
				this.DropdownActions({
					"dropdownParent": ".cs-mystart-dropdown.schools",
					"dropdownSelector": ".cs-selector",
					"dropdown": ".cs-dropdown",
					"dropdownList": ".cs-dropdown-list"
				});
			}
		},

