                            if($(window).scrollTop() > 28) {
                            	$(".gb-page.left").removeClass("contrast-active").removeAttr("style").css("top","0");
                            } else {
                            	$(".gb-page.left").addClass("contrast-active").removeAttr("style").css("height",windowHeight);
                            }
                        }
                    break;
                    case "768": case "640": case "480": case "320":
                        var navOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav").removeClass("sticky").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        if($(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "37px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "Body": function() {
        	// FOR SCOPE
            var _this = this;

            // ADD CLASS TO MYSTART IF DASHBOARD IS ACTIVE
            if($("#dashboard-sidebar").length) {
                $("#gb-page").addClass("dashboard-active");
            }

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // SUBPAGE
            // USE CHANNEL NAME FOR PAGE NAV HEADER IF ONE DOESN'T EXIST
            if(!$("div.sp-column.one .ui-widget-header h1").length > 0) {
                $("div.sp-column.one .ui-widget-header").append("
");
            }

            //NAVIGATION HOVER
            $(".ui-widget.app.navigation .ui-widget-detail > li > a").hover(function() {
            	var thisNavLink = this;
                $(thisNavLink).parent().addClass("cs-hover");
            }, function() {
            	var thisNavLink = this;
                $(thisNavLink).parent().removeClass("cs-hover");
            });

            $(".ui-widget.app").each(function() {
            	var thisApp = this;
                if($(".ui-read-more",thisApp).length) {
                    $(thisApp).find(".ui-widget-footer div.clear").before($(thisApp).find(".ui-read-more"));
                }
            });

            $(".hp-content").each(function() {
            	var thisRegion = this;
                if(!$(".ui-widget.app",thisRegion).length) {
                	$(thisRegion).remove();
                }
            });

            if(!$(".hp-content.two").length) {
            	$("#hp-content-outer").addClass("no-regionC");
            }
            if(!$(".hp-content.four").length) {
            	$("#hp-content-outer").addClass("no-regionF");
            }

            $(".ui-widget.app.navigation").each(function() {
            	var thisNavApp = this;
                if($(".ui-widget-detail > .app-level-social-follow",thisNavApp).length) {
                	$(thisNavApp).find(".app-level-social-follow").prependTo($(thisNavApp).find(".ui-widget-footer"));
                }
            });

            // REGION C
            var regionCImgSrc = jQuery.trim("/cms/lib3/TX49000037/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            var regionCImgSrcSplit = regionCImgSrc.split("/");
            var regionCImgSrcSplitLen = regionCImgSrcSplit.length;
