                $(".hp-content.two").css("background", "transparent url('/cms/lib3/TX49000037/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg') no-repeat center center/cover fixed");
            } else {
                $(".hp-content.two").css("background", "transparent url('/cms/lib3/TX49000037/Centricity/Template/20/defaults/regionC-default.jpg') no-repeat center center/cover fixed");
            }

            $('[data-region="b"] .ui-widget.app, [data-region="c"] .ui-widget.app').each(function() {
            	var thisRegionApp = this;
                if($(".ui-widget-footer > div:not(.clear)",thisRegionApp).length) {
                	$(thisRegionApp).find(".ui-widget-header").addClass("has-btns displayFlexRow");
                    if(!$(".ui-widget-header .cs-app-btns",thisRegionApp).length) {
                    	$("
