        "Shortcuts": function() {
        	var _this = this;

            // EDIT THESE TWO VARS
        	var columnNums = [2, 1, 1, 1, 1]; // [960, 768, 640, 480, 320]
            var selector = "[data-region='b'] div.ui-widget.app.siteshortcuts, [data-region='c'] div.ui-widget.app.siteshortcuts";

            // RETURN BREAKPOINT INDEX
            var bp = function() {
                switch(_this.GetBreakPoint()) {
                    case "desktop": return 0; break;
                    case "768": return 1; break;
                    case "640": return 2; break;
                    case "480": return 3; break;
                    case "320": return 4; break;
                }
            }

            // SET COLUMN NUM AND OTHER VARS
            var columnNum = columnNums[bp()];
            var endRange;

            $(selector).each(function() {
            	// RETURN THE LI'S TO THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).append($(".site-shortcuts-column > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".site-shortcuts-columns", this).remove();

                // GET SHORTCUT NUM
            	var shortcutNum = $(".ui-widget-detail > ul.site-shortcuts > li", this).length;

                // ADD COLUMN CONTAINER
                $(".ui-widget-detail", this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                	// KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < shortcutNum) {
                		// IF shortcutNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                    	// WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN
                        if((shortcutNum / columnNum) % 1 > 0.0 && (shortcutNum / columnNum) % 1 < 0.5 && i == 0) {
                            endRange = Math.round(shortcutNum / columnNum) + 1;
                        } else if((shortcutNum / columnNum) % 1 == 0.5 && i >= (columnNum / i)) {
                        	endRange = Math.round(shortcutNum / columnNum) - 1;
                        } else {
                            endRange = Math.round(shortcutNum / columnNum)
                        }

                        // ADD THE COLUMN UL
                        $(".site-shortcuts-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        $(".site-shortcuts-column.column-" + (i + 1), this).append($(".ui-widget-detail > ul.site-shortcuts > li:nth-child(n+1):nth-child(-n+" + endRange + ")", this));
                	}
                }

                if($(".app-level-social-follow",this).length) {
                	$(this).find(".ui-widget-header").addClass("has-btns displayFlexRow");
                    if(!$(".ui-widget-header .cs-app-btns",this).length) {
                    	$("
").appendTo($(this).find(".ui-widget-header.has-btns"));
                    }
                    if($(this).find(".ui-widget-header").hasClass("ui-helper-hidden")) {
                    	$(this).find(".ui-widget-header").removeClass("ui-helper-hidden").addClass("no-header");
                    }
                    if(!$(".cs-app-btns .app-level-social-follow",this).length) {
                		$(this).find(".app-level-social-follow").appendTo($(this).find(".ui-widget-header.has-btns .cs-app-btns"));
                    }
                }

                // HIDE THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).hide();
            });
        },

        "TabbedApps": function() {
        	if($("#sw-content-container5.ui-hp .ui-widget.app").length >= 2) {
        		$("#sw-content-container5.ui-hp").tabBlocks();
            } else if($("#sw-content-container5.ui-hp .ui-widget.app").length == 1) {
            	$(".hp-content.four .tabbed-region").addClass("no-tabs");
            } else {
            	$(".hp-content.four").remove();
            }
        },

        "PageNavColumns": function() {
        	var _this = this;

            // EDIT THESE TWO VARS
        	var columnNums = [2, 2, 2, 2, 2]; // [960, 768, 640, 480, 320]
            var selector = ".sp-column.one div.ui-widget.app.pagenavigation";

            // RETURN BREAKPOINT INDEX
            var bp = function() {
                switch(_this.GetBreakPoint()) {
                    case "desktop": return 0; break;
                    case "768": return 1; break;
                    case "640": return 2; break;
                    case "480": return 3; break;
                    case "320": return 4; break;
                }
            }

            var columnNum = columnNums[bp()];
            var endRange;

            // SET COLUMN NUM AND OTHER VARS
            var columnNum = columnNums[bp()];
            var endRange;

            $(selector).each(function() {
            	// RETURN THE LI'S TO THE ORIGINAL UL
                $(".ui-widget-detail > ul.page-navigation", this).append($(".page-navigation > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".site-shortcuts-columns", this).remove();

                // GET SHORTCUT NUM
            	var shortcutNum = $(".ui-widget-detail > ul.page-navigation > li", this).length;

                // ADD COLUMN CONTAINER
                $(".ui-widget-detail", this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                	// KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < shortcutNum) {
                		// IF shortcutNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                    	// WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN
                        if((shortcutNum / columnNum) % 1 > 0.0 && (shortcutNum / columnNum) % 1 < 0.5 && i == 0) {
                            endRange = Math.round(shortcutNum / columnNum) + 1;
                        } else {
                            endRange = Math.round(shortcutNum / columnNum)
                        }

                        // ADD THE COLUMN UL
                        $(".site-shortcuts-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        $(".site-shortcuts-column.column-" + (i + 1), this).append($(".ui-widget-detail > ul.page-navigation > li:nth-child(n+1):nth-child(-n+" + endRange + ")", this));
                	}
                }

                // HIDE THE ORIGINAL UL
                $(".ui-widget-detail > ul.page-navigation", this).hide();
            });
        },

        "Footer": function() {
        	// FOR SCOPE
            var _this = this;

            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-schoolwires-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-schoolwires-footer.copyright").append($("#sw-footer-copyright").html());

            // REMOVE FAX IF EMPTY
            if("" != "") {
            	$(".gb-footer-contact.fax").removeClass("hidden");
            }

            // REMOVE FOOTER HEADERS IF EMPTY
            $(".footer-header").each(function() {
            	var thisHeader = this;
            	var thisHeaderText = $(thisHeader).text();
                if(thisHeaderText == "") {
                	$(thisHeader).remove();
                }
            });

            // FOOTER LINKS
            var footerLinks = [
				{
					"text": "Accessibility",
					"url": "#",
					"target": "_self"
				},
				{
					"text": "Contact Us",
					"url": "#",
					"target": "_self"
				},
				{
					"text": "Employment",
					"url": "#",
					"target": "_self"
				}
			];

			var links = '';
			$.each(footerLinks, function(index, link) {
				if(link.text != "" && link.url != "") {
					links += '
' + link.text + '';
				}
			});

			if(links.length) {
				$(links).appendTo(".gb-footer-links");
			}

            //SCROLL TO TOP
            $(".footer-scroll-btn").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                        $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                        	$("#gb-search-input").focus();
                        });
                }
            });

            $(".footer-btn").not(".footer-scroll-btn").each(function() {
            	var thisBtn = this;
            	var thisBtnText = $("span",thisBtn).text();
                var thisBtnUrl = $(thisBtn).attr("href");
                if(thisBtnText == "" || thisBtnUrl == "") {
                	$(thisBtn).remove();
                }
            });
        },

