        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : 1260,
                "imageHeight" : 700,
                "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "galleryOverlay" : false,
                "linkedElement" : [],  // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "fade",
                "transitionSpeed" : 2,
                "transitionDelay" : 6,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {
                	//WHOLE IMAGE CLICKABLE
                    if($(".mmg-slide.active .mmg-description-link.read-more", props.element).length && "false" == "true" ) {
                        var getPhotoLink = $(".mmg-slide.active .mmg-description-link.read-more", props.element).attr("href");
                        if(!$(".mmg-slide.active .mmg-image > a", props.element).length) {
                            $(".mmg-slides .mmg-slide.active img", props.element).wrap("");
                        }
                    }
                }, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {
                	if($(".mmg-description-outer .mmg-description .mmg-description-title",props.element).length || $(".mmg-description-outer .mmg-description .mmg-description-caption",props.element).length || $(".mmg-description-outer .mmg-description .mmg-description-links",props.element).length) {
                        if(!$(".mmg-description-outer .mmg-description .mmg-description-left",props.element).length) {
                            $(".mmg-description-outer .mmg-description",props.element).wrapInner("
");
                        }
                        if($(".mmg-description-outer .mmg-description .mmg-description-left .mmg-description-links",props.element).length) {
                            $(".mmg-description-outer .mmg-description .mmg-description-left .mmg-description-links",props.element).appendTo(".mmg-description-outer .mmg-description",props.element);
                        }
                    }
                }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {
                	if($(".mmg-description-outer .mmg-description .mmg-description-title",props.element).length || $(".mmg-description-outer .mmg-description .mmg-description-caption",props.element).length || $(".mmg-description-outer .mmg-description .mmg-description-links",props.element).length) {
                        if(!$(".mmg-description-outer .mmg-description .mmg-description-left",props.element).length) {
                            $(".mmg-description-outer .mmg-description",props.element).wrapInner("
");
                        }
                        if($(".mmg-description-outer .mmg-description .mmg-description-left .mmg-description-links",props.element).length) {
                            $(".mmg-description-outer .mmg-description .mmg-description-left .mmg-description-links",props.element).appendTo(".mmg-description-outer .mmg-description",props.element);
                        }
                    }

                    if($(".mmg-control.play-pause",props.element).length) {
                    	$(".mmg-control.play-pause",props.element).prependTo(".mmg-viewer",props.element);
                    }
                    if($(".mmg-control.next",props.element).length) {
                    	$(".mmg-control.next",props.element).addClass("drt-elevate-icon-font").html("").prependTo(".mmg-viewer",props.element);
                    }
                    if($(".mmg-control.back",props.element).length) {
                    	$(".mmg-control.back",props.element).addClass("drt-elevate-icon-font").html("").prependTo(".mmg-viewer",props.element);
                    }

                    if(true == true && !$("#gb-scroll-btn").length) {
                            $('').appendTo(".mmg-viewer");

                            var getWindowHeight = $(window).height();
                            var getSlideshowHeight = $(".mmg-viewer").height();
                            var getMystartHeight = $("#gb-mystart-outer").height();
                            var totalHeight = getSlideshowHeight + getSlideshowHeight;
                            var scrollTop = $(window).scrollTop();
                            var scrollBottom = scrollTop + getWindowHeight;
                            var slideshowTop = $(".mmg-viewer").offset().top;
                            var slideshowBottom = slideshowTop + $(".mmg-viewer").height();
                            var scrollDownTop = $("#gb-scroll-btn").offset().top;
                            var scrollDownBottom = scrollDownTop + 50;

                            if($("#hp-slideshow").height() > $(window).height()) {
                            	if(scrollBottom < slideshowBottom) {
                                	if(scrollDownBottom <= slideshowBottom) {
                                    	$("#gb-scroll-btn").addClass("fixed");
                                    }
                                } else {
                                    $("#gb-scroll-btn").removeClass("fixed");
                                }
                            } else {
                            	$("#gb-scroll-btn").removeClass("fixed");
                            }

                            $(window).resize(function() {
                            	if($("#hp-slideshow").height() > $(window).height()) {
                                    if(scrollBottom < slideshowBottom) {
                                        if(scrollDownBottom <= slideshowBottom) {
                                            $("#gb-scroll-btn").addClass("fixed");
                                        }
                                    } else {
                                        $("#gb-scroll-btn").removeClass("fixed");
                                    }
                                } else {
                                    $("#gb-scroll-btn").removeClass("fixed");
                                }
                            });

                            $(window).scroll(function() {
                            	var getWindowHeight = $(window).height();
                                var getSlideshowHeight = $(".mmg-viewer").height();
                                var getMystartHeight = $("#gb-mystart-outer").height();
