                        }
                    }
                } // props.element, props.mmgRecords
            });

            // SCROLL DOWN BUTTON
            $(document).on("click", "#gb-scroll-btn", function(e) {
                  e.preventDefault();

                  var scrollPosition = 0;
                  scrollPosition = $("#hp-content-outer").offset().top

                  $("html, body").animate({
                      scrollTop: scrollPosition
                  }, "slow");
            });
