        "ModEvents": function() {
            $(".ui-widget.app.upcomingevents").modEvents({
				columns     : "no",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {
            	var thisArticle = this;
				if (!$("h1.ui-article-title.sw-calendar-block-date", this).length){
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".ui-article").append(moveArticle);
					$(this).parent().remove();
				}

                var getDayText = $(".joel-day",this).text();
                getDayText = getDayText + ",";
                $(".joel-day",this).text(getDayText);
			});

            $(".ui-widget.app.upcomingevents .ui-article .ui-article-description").each(function() {
                var thisDesc = this;
                if(!$(thisDesc).find(".sw-calendar-block-time").length) {
                    $('All Day').appendTo(thisDesc);
                } else {
                    $(thisDesc).find(".sw-calendar-block-time").appendTo(thisDesc);
                }
            });

            $('.upcomingevents').each(function() {
               if(!$('.ui-article', this).length) {
                   $('.ui-articles',this).html('




There are no upcoming events to display.');
               }
            });
        },

