$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=IgIheAykp6cpjTxQ9gU2mg%3d%3d&SessionID=6c7c988e-dcfa-4bb2-86ba-e96ca26e101b&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f26%2f2020+7%3a19%3a59+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=216.213.255.230';
