div.script:
  $(function(){
	var url = 'https://wrk.pblc.it/twitter-rss-feed-generator/api/twitter2rss?url=YlpubDYyT01QS0xDUWRsRFh3UzJlanBaeUxHUytxbmp4Z2R6OUZWclNLYz0=';
	var news = $('.noticia');
	function dateFormat(pubDate) {
	  var date = new Date(pubDate);
	  var months = Array("Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio", "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre");
	  return date.getDate() + " " + months[date.getMonth()] + " " + date.getFullYear()
	}
	function loadNews(url) {
	  $.ajax({
		url: url,
		type: 'GET',
		dataType: "xml"
	  })
	  .done(function(xml) {
		var self = $(xml).find('channel item').first();
		var url = $(self).find('link').text();
		var title = $(self).find('title').text();
		var text = $(self).find('description').text();
		var date = $(self).find('pubDate').text();
		news.html('' + text + '');
	  })
	  .fail(function(){
		news.hide();
	  });
	}
	loadNews(url);
  });
