div.script:
$(document).ready(function () {
    $("#0d0d13a9-5c22-40d4-a1ed-6dc862b12ec1").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#10ad068b-7e76-441f-8d29-1cbaf4d8ac06").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#10ad068b-7e76-441f-8d29-1cbaf4d8ac06').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#10ad068b-7e76-441f-8d29-1cbaf4d8ac06').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 7 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#42c914ad-2f42-4df1-9e60-983b21208951').hide();
    jQuery('#42c914ad-2f42-4df1-9e60-983b21208951').listSolrResults({
        currentPagePath: '/content/schools/utva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '42c914ad-2f42-4df1-9e60-983b21208951',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

