        document.addEventListener('DOMContentLoaded', function (event) {
            var xhr = new XMLHttpRequest();
            xhr.open('GET', '/documents/didyouknow3.txt', true); 

            xhr.onload = function () {
                if (xhr.status == 200) {
                    var lines = xhr.responseText.split(/\r\n|\r|\n/g);

                    var element = document.getElementById('didyouknow'); 

                    if (element != null) {
                        element.innerText = lines[rand(0, lines.length)];
                    }
                }
            }

            xhr.send();
        });
        
        function rand(min, max) {
            min = Math.ceil(min);
            max = Math.floor(max);

            return Math.floor(Math.random() * (max - min)) + min;
        }

