input.script:
        $(document).ready(function () {
            var container = $("#ToolbarPlaceHolder_T4D7C709D016_ctl00_ctl00_SearchPanel");
            var btn = container.children('.search-button');
            var txt = container.children('.search-text');

            // Add data to container for retrieval 
            container.data('queryField', txt);

            container.submitSearch = function () {
                var self = $(this);
                var queryField = self.data('queryField');

                var searchTxt = queryField.val().trim();
                if (searchTxt != '') {
                    var urlBits = [self.data('resultsUrl'), '?', self.data('urlQueryParameterName'), '=', encodeURIComponent(searchTxt)];
                    window.location.href = urlBits.join('');
                }
            };

            // Prevent bubbling outside of the widget.
            container.on('click touchstart', function (e) {
                e.stopPropagation();
            });

            // Redirect to results if a search term was provided.
            btn.on('click', function () {
                container.submitSearch();
            });
            txt.on('keypress', function (e) {
                if (e.which === 13) {
                    e.stopPropagation();
                    container.submitSearch();
                }
            });
        });

