input.script:
jQuery(document).ready(function ($) {
    $('.AudienceLinks-Tabs > li').click(function () {
        var self = $(this);
        if (self.hasClass('static') == false && self.hasClass('selected') == false) {
            var activeAudience = self.attr('class');
            var parent = self.closest('.AudienceLinks');
            var tabs = parent.find('.AudienceLinks-Tabs > li');
            var panes = parent.find('.AudienceLinks-Pane');
            tabs.filter('.selected').removeClass('selected');
            panes.filter('.selected').removeClass('selected');
            self.addClass('selected');
            panes.filter('.' + activeAudience).addClass('selected');
        }
    });
});

span.script:
    $(document).ready(function () {
        ISD.SimpleSlides.Init('#TopBannerPlaceHolder_C013_ctl00_ctl00_SlideContainer', {
            width: 480,
            height: 320,
            captionFadeTime: 300,
            fadeTime: 1000,
            waitTime: 7000,
            beforeShowCaption: function ($caption) {
                var moreSel = 'a.ss-more';
                if (!$caption.is('[truncated]')) {
                    var useOpacity = !!$caption.css('opacity');
                    if ((useOpacity && $caption.css('opacity') === '0') || (!useOpacity && $caption.css('display') == 'none' )) {

                        $caption.dotdotdot({
                            height: 120,
                            tolerance: 24,
                            after: moreSel,
                            callback: function (isTruncated, orgContent) {
                                // Hide "more" link if the caption fits and we don't have a dramatically reduced image.
                                $caption.attr('truncated', true);
                                var scale = parseFloat( $caption.siblings('img').first().attr('scale') );
                                var link = $caption.find(moreSel);
                                if (!isTruncated && scale > .8) {
                                    link.hide();
                                }
                                else {
                                    // Clear local styles added by dotdotdot.
                                    link.removeAttr('style');
                                }
                            }
                        });
        
                    }
                }
            }
        });
    });

    var Schools = {

        options: {
            SchoolsList: $("#MainContentPlaceHolder_C015_ctl00_ctl00_SchoolsList")
